// ToastAnimated.jsx
import React, { useEffect, useState } from "react";
import "../styles.css"; // تأكد إنك تضيف CSS الخاص بالتحريك هنا

const ToastAnimated = ({ message, type = "success", duration = 4000, onClose }) => {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setVisible(true); // يبدأ التحريك (fade/slide down)

    const hideTimeout = setTimeout(() => {
      setVisible(false); // يبدأ الاختفاء
    }, duration);

    const removeTimeout = setTimeout(() => {
      onClose(); // ينادي الواجهة لإخفاء الـ Toast من DOM
    }, duration + 500); // +500ms عشان يعطي الوقت للـ CSS animation

    return () => {
      clearTimeout(hideTimeout);
      clearTimeout(removeTimeout);
    };
  }, [duration, onClose]);

  return (
    <div
      className={`toast-animated ${type} ${visible ? "show" : "hide"}`}
    >
      {message}
    </div>
  );
};

export default ToastAnimated;
