import React, { useState, useContext, useRef, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import CartContext from "../context/cart/cartContext";
import LoadingContext from "../context/loading/loadingContext";
import translations from "../i18n/translations";
import "../auth.css";

// Toast بسيط مع تأثير الدخول والخروج
const Toast = ({ message, type, onClose }) => {
  const [show, setShow] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setShow(false), 4000);
    return () => clearTimeout(timer);
  }, []);

  if (!show) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: "10%",
        left: "50%",
        transform: "translateX(-50%)",
        zIndex: 9999,
        backgroundColor: type === "success" ? "#28a745" : "crimson",
        color: "white",
        padding: "1rem 2rem",
        borderRadius: "1rem",
        fontFamily: "Mulish",
        fontWeight: "bold",
        textAlign: "center",
        transition: "all 0.5s",
      }}
    >
      {message}
      <span
        style={{ marginLeft: "1rem", cursor: "pointer" }}
        onClick={() => {
          setShow(false);
          onClose();
        }}
      >
        ×
      </span>
    </div>
  );
};

const Auth = () => {
  const history = useHistory();
  const { register, login, isAuthenticated } = useContext(AuthContext);
  const { loadCart } = useContext(CartContext);
  const { Loader } = useContext(LoadingContext);
  const isMounted = useRef(true);

  const [flag, setFlag] = useState(0); // 0 = login, 1 = signup
  const [signUpData, setSignUpData] = useState({
    fullName: "",
    email: "",
    password: "",
    gender: "",
    phone: "",
    passportNumber: "",
  });
  const [signInData, setSignInData] = useState({ email: "", password: "" });
  const [inputType, setInputType] = useState("password");
  const [isLoading, setIsLoading] = useState(false);
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });
  const [language, setLanguage] = useState("en"); // الافتراضية

  useEffect(() => {
    return () => { isMounted.current = false; };
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      loadCart();
      history.push("/");
    }
  }, [isAuthenticated, history, loadCart]);

  const handleSignUpChange = (e) =>
    setSignUpData({ ...signUpData, [e.target.name]: e.target.value });

  const handleSignInChange = (e) =>
    setSignInData({ ...signInData, [e.target.name]: e.target.value });

  const togglePassword = () =>
    setInputType(inputType === "password" ? "text" : "password");

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    const t = translations[language];

    if (!signUpData.fullName || !signUpData.email || !signUpData.password ||
        !signUpData.gender || !signUpData.phone || !signUpData.passportNumber) {
      setToast({ show: true, message: t.error_fill_all, type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      const res = await register(signUpData);

      // بعد نجاح التسجيل: توجيه لصفحة الرسائل
      history.push({
        pathname: "/registration-success",
        state: {
          message:
            language === "ar"
              ? "تم إنشاء الحساب! يرجى التحقق من بريدك الإلكتروني للتحقق من الحساب."
              : "Account created! Check your email to verify your account.",
        },
      });
    } catch (err) {
      const msg = err?.errors?.[0]?.message || err?.message || t.error_server;
      setToast({ show: true, message: msg, type: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignInSubmit = async (e) => {
    e.preventDefault();
    const t = translations[language];

    if (!signInData.email || !signInData.password) {
      setToast({ show: true, message: t.error_fill_all, type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      await login(signInData);
    } catch (err) {
      const msg = err?.errors?.[0]?.message || err?.message || t.error_wrong_credentials;
      setToast({ show: true, message: msg, type: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  const toggleForm = () => setFlag(flag ^ 1);

  return (
    <>
      {isLoading && <Loader />}
      {toast.show && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast({ ...toast, show: false })}
        />
      )}

      <div className={flag === 0 ? "container1" : "container1 sign-up-mode"}>
        <div className="forms-container">
          <div className="signin-signup">
            {/* Sign In */}
            <form className="sign-in-form" onSubmit={handleSignInSubmit}>
              <h2 className="title">{translations[language].sign_in}</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="email"
                  type="email"
                  placeholder={translations[language].email}
                  value={signInData.email}
                  onChange={handleSignInChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type={inputType}
                  placeholder={translations[language].password}
                  value={signInData.password}
                  onChange={handleSignInChange}
                />
              </div>
              <input type="submit" className="btn solid" value={translations[language].login} />
              <Link to="/forget">{translations[language].forgot_password}</Link>
            </form>

            {/* Sign Up */}
            <form className="sign-up-form" onSubmit={handleSignUpSubmit}>
              <h2 className="title">{translations[language].sign_up}</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="fullName"
                  type="text"
                  placeholder={translations[language].full_name}
                  value={signUpData.fullName}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-envelope" />
                <input
                  name="email"
                  type="email"
                  placeholder={translations[language].email}
                  value={signUpData.email}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field select-wrapper">
                <i className="fas fa-venus-mars" />
                <select name="gender" value={signUpData.gender} onChange={handleSignUpChange}>
                  <option value="">{translations[language].select_gender}</option>
                  <option value="male">{translations[language].male}</option>
                  <option value="female">{translations[language].female}</option>
                  <option value="other">{translations[language].other}</option>
                </select>
              </div>
              <div className="input-field">
                <i className="fas fa-phone" />
                <input
                  name="phone"
                  type="text"
                  placeholder={translations[language].phone}
                  value={signUpData.phone}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-id-card" />
                <input
                  name="passportNumber"
                  type="text"
                  placeholder={translations[language].passport_number}
                  value={signUpData.passportNumber}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input name="password"
                  type="password"
                  placeholder={translations[language].password}
                  value={signUpData.password}
                  onChange={handleSignUpChange}
                />
              </div>
              <input type="submit" className="btn" value={translations[language].sign_up} />
            </form>
          </div>
        </div>

        {/* Panels */}
        <div className="panels-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>{translations[language].new_here}</h3>
              <p>{translations[language].signup_prompt}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {translations[language].sign_up}
              </button>
            </div>
          </div>
          <div className="panel right-panel">
            <div className="content">
              <h3>{translations[language].one_of_us}</h3>
              <p>{translations[language].signin_prompt}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {translations[language].sign_in}
              </button>
            </div>
          </div>
          </div>
        </div>
    </>
  );
};

export default Auth;
