import React, { useContext, useReducer } from "react";
import AuthContext from "./authContext";
import AuthReducer from "./authReducer";
import axios from "axios";
import setAuthToken from "./setAuthToken";
import removeAuthToken from "./removeAuthToken";

const AuthState = (props) => {
  const initialState = {
    isAuthenticated: false,
    user: null,
    loading: false,
    current: 0,
    errors: [],
    verified: false,
    displayVerified: false,
    successMessage: "" // لإظهار رسالة نجاح
  };

  const [state, dispatch] = useReducer(AuthReducer, initialState);

  // تحميل بيانات المستخدم
  const userLoaded = async () => {
    if (localStorage.token) {
      setAuthToken(localStorage.token);
    }
    try {
      const config = { headers: { "Content-Type": "application/json" } };
      const res = await axios.get(
        "http://localhost:5000/api/user/auth/user", // تأكد من هذا الرابط في الباك
        config
      );
      dispatch({ type: "USER_LOADED", payload: res.data });
      setTimeout(() => {
        dispatch({ type: "REMOVE_DISPLAY_VERIFIED" });
      }, 3000);
      return 0;
    } catch (err) {
      dispatch({ type: "AUTH_ERROR" });
      return 0;
    }
  };

  // تسجيل مستخدم جديد
  const register = async (formData) => {
    const config = { headers: { "Content-Type": "application/json" } };
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/auth/register",
        formData,
        config
      );
      localStorage.setItem("token", res.data.token); // تخزين التوكن فقط
      dispatch({ type: "REGISTER_SUCCESS", payload: res.data });
      dispatch({ type: "SET_SUCCESS_MESSAGE", payload: "Registration successful!" });
      userLoaded();
    } catch (err) {
      dispatch({
        type: "REGISTER_FAIL",
        payload: err.response ? err.response.data : { msg: "Server Error" }
      });
      setTimeout(() => dispatch({ type: "REMOVE_ALERT" }), 3500);
    }
  };

  // تسجيل الدخول
  const login = async (formData) => {
    const config = { headers: { "Content-Type": "application/json" } };
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/auth/login",
        formData,
        config
      );
      localStorage.setItem("token", res.data.token);
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
      dispatch({ type: "SET_SUCCESS_MESSAGE", payload: "Login successful!" });
      userLoaded();
      setTimeout(() => dispatch({ type: "REMOVE_CURRENT" }), 3000);
      return 0;
    } catch (err) {
      if (!err.response) {
        alert("Connection Cannot Be Made to Server");
      } else {
        dispatch({
          type: "LOGIN_FAIL",
          payload: err.response.data
        });
        setTimeout(() => dispatch({ type: "REMOVE_ALERT" }), 3500);
      }
    }
  };

  const set_loading = () => {
    dispatch({ type: "SET_LOADING" });
  };

  const logout = () => {
    localStorage.removeItem("token");
    dispatch({ type: "LOGOUT" });
    setAuthToken(null);
  };

  return (
    <AuthContext.Provider
      value={{
        register,
        login,
        logout,
        isAuthenticated: state.isAuthenticated,
        loading: state.loading,
        userLoaded,
        user: state.user,
        set_loading,
        errors: state.errors,
        current: state.current,
        verified: state.verified,
        displayVerified: state.displayVerified,
        successMessage: state.successMessage
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthState;
