import React, { useState, useContext, useEffect } from "react";
import { Link, useNavigate} from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import CartContext from "../context/cart/cartContext";
import Alert from "./Alert";
import LoadingContext from "../context/loading/loadingContext";
import "../auth.css"; 
const Auth = () => {
  const { register, login, isAuthenticated, userLoaded, loading: authLoading } = useContext(AuthContext);
  const { loadCart } = useContext(CartContext);
  const { Loader } = useContext(LoadingContext);
  const navigate = useNavigate();

  const [loading, setLoading] = useState(true);
  const [flag, setFlag] = useState(0);
  const [message, setMessage] = useState(""); // رسالة بصريّة

  const [signUpData, setSignUpData] = useState({
    name: "",
    password: "",
    confirmPassword: "",
    email: "",
    number: ""
  });

  const [signInData, setSignInData] = useState({
    email: "",
    password: ""
  });

  useEffect(() => {
    if (!isAuthenticated && localStorage.getItem("token")) {
      userLoaded();
    } else if (isAuthenticated) {
      loadCart();
      navigate("/");
    }
    setLoading(false);
  }, [isAuthenticated]);

  const handleSignInData = (e) => {
    setSignInData({ ...signInData, [e.target.name]: e.target.value });
  };

  const handleSignUpData = (e) => {
    setSignUpData({ ...signUpData, [e.target.name]: e.target.value });
  };

  const clickHandle = () => {
    setFlag((prev) => prev ^ 1);
    setMessage("");
  };

  const handleSignIn = async (e) => {
    e.preventDefault();
    setLoading(true);
    const success = await login(signInData);
    if (success) setMessage("✅ Login successful!");
    else setMessage("❌ Login failed!");
    setLoading(false);
  };

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    const { name, email, password, confirmPassword, number } = signUpData;
    if (!name || !email || !password || !confirmPassword || !number) {
      setMessage("❌ Please fill all fields");
      return;
    }
    if (password !== confirmPassword) {
      setMessage("❌ Passwords do not match");
      return;
    }
    setLoading(true);
    const success = await register(signUpData);
    if (success) setMessage("✅ Registration successful!");
    else setMessage("❌ Registration failed!");
    setLoading(false);
  };

  return (
    <>
      {loading || authLoading ? <Loader /> : null}
      <div className={flag === 0 ? "container1" : "container1 sign-up-mode"}>
        {message && <div className="message">{message}</div>}
        <div className="forms-container" style={{ width: "100%" }}>
          <div className="signin-signup">
            <form className="sign-in-form" onSubmit={handleSignIn}>
              <h2 className="title">Sign in</h2>
              <input name="email" type="email" placeholder="Email" onChange={handleSignInData} />
              <input name="password" type="password" placeholder="Password" onChange={handleSignInData} />
              <input type="submit" value="Login" className="btn solid" />
              <Link to="/forget">Forgot Password?</Link>
            </form>

            <form className="sign-up-form" onSubmit={handleSignUpSubmit}>
              <h2 className="title">Sign up</h2>
              <input name="name" type="text" placeholder="Name" value={signUpData.name} onChange={handleSignUpData} />
              <input name="email" type="email" placeholder="Email" value={signUpData.email} onChange={handleSignUpData} />
              <input name="number" type="text" placeholder="Contact Number" value={signUpData.number} onChange={handleSignUpData} />
              <input name="password" type="password" placeholder="Password" value={signUpData.password} onChange={handleSignUpData} />
              <input name="confirmPassword" type="password" placeholder="Confirm Password" value={signUpData.confirmPassword} onChange={handleSignUpData} />
              <input type="submit" value="Sign up" className="btn" />
            </form>
          </div>
        </div>

        <div className="panels-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>New here?</h3>
              <p>Then Sign Up and Start Ordering!</p>
              <button className="btn transparent" onClick={clickHandle}>Sign up</button>
            </div>
          </div>
          <div className="panel right-panel">
            <div className="content">
              <h3>One of us?</h3>
              <p>Then Sign In and get Started!</p>
              <button className="btn transparent" onClick={clickHandle}>Sign in</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Auth;
