import React, { useState, useContext, useEffect, useRef } from "react";
import "../styles.css";
import "../mobile.css";
import AuthContext from "../context/auth/authContext";

const Auth = () => {
  const { register, login, errors, isAuthenticated } = useContext(AuthContext);

  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    password: "",
    gender: "",
    phone: "",
    passportNumber: "",
  });

  const [isLogin, setIsLogin] = useState(true); // لتبديل بين تسجيل الدخول والتسجيل

  const isMounted = useRef(true);

  useEffect(() => {
    return () => {
      isMounted.current = false; // منع أي تحديث بعد فك المكون
    };
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLogin) {
      try {
        await login({
          email: formData.email,
          password: formData.password,
        });
      } catch (err) {
        console.log(err);
      }
    } else {
      try {
        await register(formData);
      } catch (err) {
        console.log(err);
      }
    }
  };

  return (
    <div className="container1">
      <div className={`form-container ${isLogin ? "sign-in-mode" : "sign-up-mode"}`}>
        <form onSubmit={handleSubmit} className="auth-form">
          <h2>{isLogin ? "Sign In" : "Sign Up"}</h2>

          {!isLogin && (
            <>
              <input
                type="text"
                name="fullName"
                placeholder="Full Name"
                value={formData.fullName}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="gender"
                placeholder="Gender"
                value={formData.gender}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="passportNumber"
                placeholder="Passport Number"
                value={formData.passportNumber}
                onChange={handleChange}
                required
              />
            </>
          )}

          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />

          <button type="submit">{isLogin ? "Sign In" : "Sign Up"}</button>

          <p
            className="toggle-auth-mode"
            onClick={() => setIsLogin(!isLogin)}
          >
            {isLogin
              ? "Don't have an account? Sign Up"
              : "Already have an account? Sign In"}
          </p>

          {errors && errors.length > 0 && (
            <div className="auth-errors">
              {errors.map((err, idx) => (
                <p key={idx}>{err.msg || err}</p>
              ))}
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default Auth;
