import React, { useContext } from "react";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import "../styles.css";
import { Link } from "react-router-dom";
import { Navbar, Nav } from "react-bootstrap";
import AuthContext from "../context/auth/authContext";

export default function Navbar1(props) {
  const authContext = useContext(AuthContext);

  if (!authContext) return null; // حماية لو ما فيه Provider

  const { logout, isAuthenticated } = authContext;

  const handleLogout = () => {
    logout();
  };

  return (
    <Navbar
      style={{
        position: "sticky",
        top: "0",
        zIndex: "100000",
        display: "flex",
        justifyContent: "space-between",
        fontFamily: "Mulish",
      }}
      bg="primary"
      variant="dark"
    >
      <Navbar.Brand className="navbar titl">
        <Link to="/" style={{ color: "white", fontFamily: "Mulish" }}>
          Food Eazy <i className="fas fa-utensils" />
        </Link>
      </Navbar.Brand>

      <Nav>
        <div className="navLogout">
          <Link to="/" style={{ color: "white", textDecoration: "none" }}>
            <span className="bg_grey">Home</span>
          </Link>
        </div>

        {isAuthenticated && (
          <>
            <div className="navLogout">
              <Link
                to="/order"
                style={{ color: "white", textDecoration: "none" }}
              >
                <span className="bg_grey">Orders</span>
              </Link>
            </div>

            <div className="navLogout">
              <Link
                to="/addproduct"
                style={{ color: "white", textDecoration: "none" }}
              >
                <span className="bg_grey">Add Items</span>
              </Link>
            </div>

            <div className="navLogout">
              <Link
                to="/deleteitems"
                style={{ color: "white", textDecoration: "none" }}
              >
                <span className="bg_grey">Delete Items</span>
              </Link>
            </div>

            <div className="navLogout">
              <Link
                to="/contact"
                style={{ color: "white", textDecoration: "none" }}
              >
                <span className="bg_grey">Contact Us</span>
              </Link>
            </div>

            <div className="navLogout">
              <button
                type="button"
                onClick={handleLogout}
                style={{ border: "none", outline: "none", color: "white" }}
              >
                Logout <ExitToAppIcon />
              </button>
            </div>
          </>
        )}

        <span className="hamburger">
          <i
            onClick={props.changeDisplay}
            className="fa fa-bars"
            aria-hidden="true"
          />
        </span>
      </Nav>
    </Navbar>
  );
}
