import React, { useContext, useState } from "react";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import { Link } from "react-router-dom";
import { Navbar, Nav } from "react-bootstrap";
import AuthContext from "../context/auth/authContext";

export default function Navbar1({ changeLanguage }) {
  const authContext = useContext(AuthContext);
  const { logout, isAuthenticated } = authContext;

  const handleLogout = () => {
    logout();
    window.location.href = "/auth";
  };

  const [language, setLanguage] = useState("en");

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
    if (changeLanguage) changeLanguage(e.target.value);
  };

  return (
    <Navbar
      style={{
        position: "sticky",
        top: "0",
        zIndex: 1000,
        display: "flex",
        justifyContent: "space-between",
        backgroundColor: "#b3e0ff", // أزرق فاتح باهت
        fontFamily: "Mulish",
        padding: "0.5rem 1rem",
      }}
      variant="light"
    >
      <Navbar.Brand>
        <Link to="/" style={{ color: "#004466", fontWeight: "bold", fontSize: "1.3rem" }}>
          Syrian Flight <i className="fas fa-plane-departure" />
        </Link>
      </Navbar.Brand>

      <Nav style={{ display: "flex", alignItems: "center" }}>
        <Link to="/" style={{ margin: "0 0.5rem", textDecoration: "none", color: "#004466" }}>
          Home
        </Link>

        {isAuthenticated && (
          <>
            <Link to="/order" style={{ margin: "0 0.5rem", textDecoration: "none", color: "#004466" }}>
              Orders
            </Link>
            <Link to="/addproduct" style={{ margin: "0 0.5rem", textDecoration: "none", color: "#004466" }}>
              Add Items
            </Link>
            <Link to="/deleteitems" style={{ margin: "0 0.5rem", textDecoration: "none", color: "#004466" }}>
              Delete Items
            </Link>
            <Link to="/contact" style={{ margin: "0 0.5rem", textDecoration: "none", color: "#004466" }}>
              Contact Us
            </Link>
            <button
              onClick={handleLogout}
              style={{
                margin: "0 0.5rem",
                border: "none",
                background: "transparent",
                color: "#004466",
                cursor: "pointer",
                display: "flex",
                alignItems: "center",
              }}
            >
              Logout <ExitToAppIcon />
            </button>
          </>
        )}

        {/* قائمة اختيار اللغة */}
        <div style={{ marginLeft: "1rem" }}>
          <select
            value={language}
            onChange={handleLanguageChange}
            style={{
              padding: "0.4rem 0.8rem",
              borderRadius: "0.5rem",
              border: "none",
              backgroundColor: "#cce7ff",
              color: "#004466",
              fontWeight: "500",
              cursor: "pointer",
              fontFamily: "Mulish",
              transition: "all 0.3s ease",
            }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#99d1ff")}
            onMouseOut={(e) => (e.target.style.backgroundColor = "#cce7ff")}
          >
            <option value="en">English</option>
            <option value="ar">العربية</option>
          </select>
        </div>
      </Nav>
    </Navbar>
  );
}
