import React, { useState } from "react";
import { Form, Toast, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import axios from "axios";
import "../styles.css";

const Forget = () => {
  const history = useHistory();
  const [mail, setMail] = useState("");
  const [status, setStatus] = useState({ val: 0, msg: "", type: "" });

  const Alert = () => (
    <Toast style={{ position: "fixed", right: "0", zIndex: "9899899" }}>
      <Toast.Header
        style={{
          background: status.type === "darkGreen" ? "green" : "red",
          color: "white",
          fontWeight: "bold"
        }}
      >
        <strong className="mr-auto">
          {status.type === "darkGreen" ? "Success" : "Fail"}
        </strong>
      </Toast.Header>
      <Toast.Body>{typeof status.msg === "string" ? status.msg : JSON.stringify(status.msg)}</Toast.Body>
    </Toast>
  );

  const handleChange = (e) => setMail(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!mail) return alert("Please enter your email");

    const config = { headers: { "Content-Type": "application/json" } };
    try {
      await axios.post("https://foodeazy.herokuapp.com/forget", { email: mail }, config);
      setStatus({ val: 1, msg: "Check your email for the reset code!", type: "darkGreen" });

      // بعد 3 ثواني انتقل لصفحة إعادة ضبط كلمة المرور
      setTimeout(() => {
        history.push("/reset-password", { email: mail });
      }, 3000);
    } catch (e) {
      setStatus({ val: 1, msg: e.response?.data || "Error occurred", type: "red" });
      setTimeout(() => setStatus({ val: 0, msg: "", type: "" }), 3000);
    }
  };

  return (
    <>
      {status.val ? <Alert /> : null}
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>
        Forgot your Password?
      </h4>
      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            Enter your email address and we'll send you a link to reset your password.
          </span>
        </p>
        <br />
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label
              style={{
                fontFamily: "Poppins",
                fontWeight: "550"
              }}
            >
              Email Address <MailOutlineIcon />
            </Form.Label>
            <Form.Control
              type="email"
              style={{ width: "125%" }}
              placeholder="e.g. email@domain.com"
              onChange={handleChange}
            />
          </Form.Group>

          <Button
            className="btn btn-primary"
            variant="primary"
            type="submit"
            style={{ borderRadius: "1vh", textTransform: "capitalize" }}
          >
            Reset Password
          </Button>
        </Form>
      </div>
    </>
  );
};

export default Forget;
