import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import API from "../api/axios";
import translations from "../i18n/translations";
import Toast from "./toastAnimated";
import "../styles.css";

const ResetPassword = () => {
  const history = useHistory();

  // Toast state (نفس الشي يلي مستخدم بالأوث)
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });

  // Form data state
  const [formData, setFormData] = useState({
    email: "",
    resetCode: "",
    newPassword: "",
  });

  const [isLoading, setIsLoading] = useState(false); // Loader state

  // Handle input change
  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // Handle submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    const { email, resetCode, newPassword } = formData;
    if (!email || !resetCode || !newPassword) {
      setToast({ show: true, message: translations.en.error_fill_all, type: "error" });
      return;
    }

    setIsLoading(true);

    try {
      // نرسل اللغة في الهيدر مثل الأوث
      const lang = localStorage.getItem("lang") || "en";

      const res = await API.post(
        "/user/reset-password",
        formData,
        { headers: { lang } }
      );

      // عرض الرسالة من الباك حسب اللغة
      setToast({
        show: true,
        message: res.data?.message || translations[lang].success,
        type: "success",
      });

      // بعد ٣ ثواني ينتقل لصفحة تسجيل الدخول
      setTimeout(() => {
        history.push("/auth");
      }, 3000);

    } catch (err) {
      const lang = err.response?.data?.lang || localStorage.getItem("lang") || "en";
      const msg = err.response?.data?.message || translations[lang].error;
      setToast({ show: true, message: msg, type: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  const lang = localStorage.getItem("lang") || "en";
  const t = translations[lang];

  return (
    <div className="reset-password-container" style={{ marginTop: "5vh" }}>
      {/* Toast */}
      {toast.show && (
        <Toast
          message={toast.message}
          type={toast.type}
          show={toast.show}
          onClose={() => setToast({ ...toast, show: false })}
        />
      )}

      {/* عنوان الصفحة */}
      <h3 style={{ textAlign: "center", fontFamily: "Mulish" }}>
        {t.resetPasswordTitle}
      </h3>

      {/* الفورم */}
      <Form onSubmit={handleSubmit} style={{ maxWidth: "400px", margin: "auto" }}>
        <Form.Group>
          <Form.Label>
            {t.emailLabel} <MailOutlineIcon />
          </Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder={t.placeholder}
            required
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>
            {t.verificationCodeLabel} <LockIcon />
          </Form.Label>
          <Form.Control
            type="text"
            name="resetCode"
            value={formData.resetCode}
            onChange={handleChange}
            placeholder={t.verificationCodePlaceholder}
            required
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>
            {t.newPasswordLabel} <LockIcon />
          </Form.Label>
          <Form.Control
            type="password"
            name="newPassword"
            value={formData.newPassword}
            onChange={handleChange}
            placeholder={t.newPasswordPlaceholder}
            required
          />
        </Form.Group>

        <Button
          type="submit"
          disabled={isLoading}
          style={{
            width: "100%",
            borderRadius: "1rem",
            marginTop: "1rem",
            backgroundColor: "#007bff",border: "none",
          }}
        >
          {isLoading ? t.loading : t.btnReset}
        </Button>
      </Form>
    </div>
  );
};

export default ResetPassword;
