import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { useLocation, useHistory } from "react-router-dom";
import LockIcon from "@material-ui/icons/Lock";
import "../styles.css";

const ResetPassword = () => {
  const location = useLocation();
  const history = useHistory();
  const email = location.state?.email || "";
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!password || !confirmPassword) return alert("Fill all fields");
    if (password !== confirmPassword) return alert("Passwords do not match");

    // هنا ممكن تضيف طلب API لإعادة ضبط كلمة المرور
    alert(`Password reset for ${email} successful!`);
    history.push("/auth"); // العودة لصفحة تسجيل الدخول
  };

  return (
    <div className="forget">
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>Reset Password</h4>
      <p>
        <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
        <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
          Enter your new password for {email}
        </span>
      </p>
      <br />
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="New password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </Form.Group>
        <Button
          className="btn btn-primary"
          variant="primary"
          type="submit"
          style={{ borderRadius: "1vh", textTransform: "capitalize" }}
        >
          Reset Password
        </Button>
      </Form>
    </div>
  );
};

export default ResetPassword;
