import React, { useState, useContext, useEffect } from "react";
import { Link, useHistory} from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import CartContext from "../context/cart/cartContext";
import Alert from "./Alert";
import LoadingContext from "../context/loading/loadingContext";
import "../auth.css"; 
const Auth = () => {
  const history = useHistory();
  const { register, login, isAuthenticated } = useContext(AuthContext);
  const { loadCart } = useContext(CartContext);
  const { Loader } = useContext(LoadingContext);

  const [flag, setFlag] = useState(0);
  const [signUpData, setSignUpData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    number: ""
  });
  const [signInData, setSignInData] = useState({
    email: "",
    password: ""
  });
  const [inputType, setInputType] = useState("password");
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (isAuthenticated) {
      loadCart();
      history.push("/");
    }
  }, [isAuthenticated, history, loadCart]);

  const handleSignUpChange = (e) => setSignUpData({ ...signUpData, [e.target.name]: e.target.value });
  const handleSignInChange = (e) => setSignInData({ ...signInData, [e.target.name]: e.target.value });
  const togglePassword = () => setInputType(inputType === "password" ? "text" : "password");

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    const { name, email, password, confirmPassword, number } = signUpData;
    if (!name || !email || !password || !confirmPassword || !number) return alert("Please fill all fields");
    if (password !== confirmPassword) return alert("Passwords do not match");
    setIsLoading(true);
    await register(signUpData);
    setIsLoading(false);
  };

  const handleSignInSubmit = async (e) => {
    e.preventDefault();
    const { email, password } = signInData;
    if (!email || !password) return alert("Please fill all fields");
    setIsLoading(true);
    await login(signInData);
    setIsLoading(false);
  };

  const toggleForm = () => setFlag(flag ^ 1);

  return (
    <>
      <Alert />
      {isLoading ? (
        <Loader />
      ) : (
        <div className={flag === 0 ? "container1" : "container1 sign-up-mode"}>
          <div className="forms-container">
            <div className="signin-signup">

              {/* Sign In Form */}
              <form className="sign-in-form" onSubmit={handleSignInSubmit}>
                <h2 className="title">Sign in</h2>
                <div className="input-field">
                  <i className="fas fa-user" />
                  <input
                    name="email"
                    type="email"
                    placeholder="Email"
                    value={signInData.email}
                    onChange={handleSignInChange}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-lock" />
                  <input
                    name="password"
                    type={inputType}
                    placeholder="Password"
                    value={signInData.password}
                    onChange={handleSignInChange}
                  />
                  <button type="button" onClick={togglePassword}>
                    {inputType === "password" ? "Show" : "Hide"}
                  </button>
                </div>
                <input type="submit" value="Login" className="btn solid" />
                <Link to="/forget">Forgot Password?</Link>
              </form>

              {/* Sign Up Form */}
              <form className="sign-up-form" onSubmit={handleSignUpSubmit}>
                <h2 className="title">Sign up</h2>
                <div className="input-field">
                  <i className="fas fa-user" />
                  <input
                    name="name"
                    type="text"
                    placeholder="Name"
                    value={signUpData.name}
                    onChange={handleSignUpChange}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-envelope" />
                  <input
                    name="email"
                    type="email"
                    placeholder="Email"
                    value={signUpData.email}
                    onChange={handleSignUpChange}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-phone" />
                  <input
                    name="number"
                    type="text"
                    placeholder="Contact Number"
                    value={signUpData.number}
                    onChange={handleSignUpChange}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-lock" />
                  <input
                    name="password"
                    type="password"
                    placeholder="Password"
                    value={signUpData.password}
                    onChange={handleSignUpChange}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-lock" />
                  <input
                    name="confirmPassword"
                    type="password"
                    placeholder="Confirm Password"
                    value={signUpData.confirmPassword}
                    onChange={handleSignUpChange}
                  />
                </div>
                <input type="submit" className="btn" value="Sign up" />
              </form>

            </div>
          </div>

          <div className="panels-container">
            <div className="panel left-panel">
              <div className="content">
                <h3>New here?</h3>
                <p>Then Sign Up and Start Ordering!</p>
                <button className="btn transparent" onClick={toggleForm}>
                  Sign up
                </button>
              </div>
            </div>
            <div className="panel right-panel">
              <div className="content">
                <h3>One of us?</h3>
                <p>Then Sign In and get Started!</p>
                <button className="btn transparent" onClick={toggleForm}>
                  Sign in
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Auth;
