import React, { useContext, useReducer } from "react";
import AuthContext from "./authContext";
import AuthReducer from "./authReducer";
import axios from "axios";
import setAuthToken from "./setAuthToken";
import removeAuthToken from "./removeAuthToken";

const AuthState = (props) => {
  const initialState = {
    isAuthenticated: false,
    user: null,
    loading: false,
    current: 0,
    errors: [],
    verified: false,
    displayVerified: false
  };

  const [state, dispatch] = useReducer(AuthReducer, initialState);

  const userLoaded = async () => {
    if (localStorage.token) {
      setAuthToken(localStorage.token);
    }
    try {
      const res = await axios.get("http://localhost:5000/api/user/auth/login", {
        headers: { "Content-Type": "application/json" }
      });
      dispatch({ type: "USER_LOADED", payload: res.data });
      setTimeout(() => dispatch({ type: "REMOVE_DISPLAY_VERIFIED" }), 3000);
    } catch (err) {
      dispatch({ type: "AUTH_ERROR" });
    }
  };

  const register = async (formData) => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/auth/register",
        formData,
        { headers: { "Content-Type": "application/json" } }
      );
      dispatch({ type: "REGISTER_SUCCESS", payload: res.data });
      localStorage.setItem("token", res.data);
      userLoaded();
    } catch (err) {
      dispatch({
        type: "REGISTER_FAIL",
        payload: parseErrors(err)
      });
      setTimeout(() => dispatch({ type: "REMOVE_ALERT" }), 3500);
    }
  };

  const login = async (formData) => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/auth/login",
        formData,
        { headers: { "Content-Type": "application/json" } }
      );
      localStorage.setItem("token", res.data);
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
      userLoaded();
      setTimeout(() => dispatch({ type: "REMOVE_CURRENT" }), 3000);
    } catch (err) {
      const errors = err.response ? parseErrors(err) : ["Cannot connect to server"];
      dispatch({ type: "LOGIN_FAIL", payload: errors });
      setTimeout(() => dispatch({ type: "REMOVE_ALERT" }), 3500);
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    dispatch({ type: "LOGOUT" });
    setAuthToken(null);
  };

  const set_loading = () => dispatch({ type: "SET_LOADING" });

  // تحويل الأخطاء إلى نصوص
  const parseErrors = (err) => {
    if (!err.response) return ["Unknown error"];
    const data = err.response.data;
    if (data.errors && Array.isArray(data.errors)) return data.errors.map(e => e.msg || JSON.stringify(e));
    if (data.message) return [data.message];
    return [JSON.stringify(data)];
  };

  return (
    <AuthContext.Provider
      value={{
        register,
        login,
        logout,
        isAuthenticated: state.isAuthenticated,
        loading: state.loading,
        userLoaded,
        user: state.user,
        set_loading,
        errors: state.errors,
        current: state.current,
        verified: state.verified,
        displayVerified: state.displayVerified
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthState;
