import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { useToast } from "../components/GlobalToast"; // ÿßÿ≥ÿ™ÿØÿπÿßÿ° ÿßŸÑÿ™Ÿàÿ≥ÿ™
import "../styles.css";

const Forget = () => {
  const [mail, setMail] = useState("");
  const history = useHistory();
  const { showToast } = useToast();

  const handleChange = (e) => {
    setMail(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const formData = { email: mail };

    try {
      // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ®ÿ±ŸäÿØ ŸÑŸÑÿ®ÿßŸÉ
      await axios.post("http://localhost:5000/api/user/forget", formData, config);

      // ÿ•ÿ¥ÿπÿßÿ± ŸÜÿ¨ÿßÿ≠
      showToast("üì© ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ŸÖÿ≤ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿπŸäŸäŸÜ ÿ•ŸÑŸâ ÿ®ÿ±ŸäÿØŸÉ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä", "success");

      // ÿ®ÿπÿØ Ÿ£ ÿ´ŸàÿßŸÜŸä Ÿäÿ±Ÿàÿ≠ ŸÑÿµŸÅÿ≠ÿ© reset
      setTimeout(() => {
        history.push("/reset-password");
      }, 3000);

    } catch (err) {
      // ÿ•ÿ¥ÿπÿßÿ± ÿÆÿ∑ÿ£
      const msg =
        err.response?.data?.message || "‚ö†Ô∏è ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑÿå ÿ≠ÿßŸàŸÑ ŸÖÿ¨ÿØÿØÿßŸã";
      showToast(msg, "error");
    }
  };

  return (
    <>
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>
        Forgot your Password?
      </h4>
      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            {" "}
            Enter your email address and we'll send you a code to reset your
            password.
          </span>
        </p>
        <br />
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label
              style={{
                fontFamily: "Poppins",
                align: "left",
                fontWeight: "550",
              }}
            >
              Email Address <MailOutlineIcon />
            </Form.Label>
            <Form.Control
              type="email"
              style={{ width: "125%" }}
              placeholder="e.g. email@domain.com"
              onChange={handleChange}
              required
            />
          </Form.Group>

          <Button
            className="btn btn-primary"
            variant="primary"
            type="submit"
            style={{
              borderRadius: "1vh",
              textTransform: "capitalize",
            }}
          >
            Reset Password
          </Button>
        </Form>
      </div>
    </>
  );
};

export default Forget;
