import React, { useState, useContext, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { AuthContext } from "../context/auth/authState";
import "../auth.css";


const Auth = () => {
  const history = useHistory();
  const { register, login, displayVerified, isAuthenticated } = useContext(AuthContext);

  const [flag, setFlag] = useState(0); // 0 = login, 1 = signup
  const [signUpData, setSignUpData] = useState({
    fullName: "",
    email: "",
    password: "",
    gender: "",
    phone: "",
    passportNumber: "",
  });
  const [signInData, setSignInData] = useState({ email: "", password: "" });
  const [inputType, setInputType] = useState("password");
  const [showNotice, setShowNotice] = useState(false);

  const toggleForm = () => setFlag(flag ^ 1);
  const togglePassword = () => setInputType(inputType === "password" ? "text" : "password");

  const handleSignUpChange = (e) => setSignUpData({ ...signUpData, [e.target.name]: e.target.value });
  const handleSignInChange = (e) => setSignInData({ ...signInData, [e.target.name]: e.target.value });

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    await register(signUpData);
  };

  const handleSignInSubmit = async (e) => {
    e.preventDefault();
    await login(signInData);
  };

  // عند عرض مربع التحقق
  useEffect(() => {
    if (displayVerified) {
      setShowNotice(true);
      const timer = setTimeout(() => {
        setShowNotice(false);
        setFlag(0); // الرجوع لشاشة تسجيل الدخول
      }, 10000);
      return () => clearTimeout(timer);
    }
  }, [displayVerified]);

  // إذا تم تسجيل الدخول بنجاح
  useEffect(() => {
    if (isAuthenticated) {
      history.push("/"); // دخول للموقع
    }
  }, [isAuthenticated, history]);

  return (
    <>
      {showNotice && (
        <div className="verification-notice">
          <h3>Check Your Email</h3>
          <p>A verification link has been sent to your email. Please verify your account before login.</p>
        </div>
      )}

      <div className={flag === 0 ? "container1" : "container1 sign-up-mode"}>
        <div className="forms-container">
          <div className="signin-signup">
            {/* ===== Sign In Form ===== */}
            <form className="sign-in-form" onSubmit={handleSignInSubmit}>
              <h2 className="title">Sign in</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="email"
                  type="email"
                  placeholder="Email"
                  value={signInData.email}
                  onChange={handleSignInChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type={inputType}
                  placeholder="Password"
                  value={signInData.password}
                  onChange={handleSignInChange}
                />
                <button type="button" onClick={togglePassword}>
                  {inputType === "password" ? "Show" : "Hide"}
                </button>
              </div>
              <input type="submit" value="Login" className="btn solid" />
              <Link to="/forget">Forgot Password?</Link>
            </form>

            {/* ===== Sign Up Form ===== */}
            <form className="sign-up-form" onSubmit={handleSignUpSubmit}>
              <h2 className="title">Sign up</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="fullName"
                  type="text"
                  placeholder="Full Name"
                  value={signUpData.fullName}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-envelope" />
                <input
                  name="email"
                  type="email"placeholder="Email"
                  value={signUpData.email}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-venus-mars" />
                <select
                  name="gender"
                  value={signUpData.gender}
                  onChange={handleSignUpChange}
                  className="input-field-select"
                >
                  <option value="">Select Gender</option>
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                  <option value="other">Other</option>
                </select>
              </div>
              <div className="input-field">
                <i className="fas fa-phone" />
                <input
                  name="phone"
                  type="text"
                  placeholder="Phone"
                  value={signUpData.phone}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-id-card" />
                <input
                  name="passportNumber"
                  type="text"
                  placeholder="Passport Number"
                  value={signUpData.passportNumber}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type="password"
                  placeholder="Password"
                  value={signUpData.password}
                  onChange={handleSignUpChange}
                />
              </div>
              <input type="submit" className="btn" value="Sign up" />
            </form>
          </div>
        </div>

        <div className="panels-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>New here?</h3>
              <p>Then Sign Up and Start Ordering!</p>
              <button className="btn transparent" onClick={toggleForm}>
                Sign up
              </button>
            </div>
          </div>
          <div className="panel right-panel">
            <div className="content">
              <h3>One of us?</h3>
              <p>Then Sign In and get Started!</p>
              <button className="btn transparent" onClick={toggleForm}>
                Sign in
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Auth;
