import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { CartContext } from "../context/CartContext";
import { LoadingContext } from "../context/LoadingContext";
import Alert from "../components/Alert";

const API_URL = "http://localhost:5000";

const Auth = (props) => {
  const { register, login, userLoaded, isAuthenticated } = useContext(AuthContext);
  const { loadCart } = useContext(CartContext);
  const { Loader } = useContext(LoadingContext);

  const [loading, setLoading] = useState(1);
  const [flag, setFlag] = useState(0);
  const [inputType, setInputType] = useState("password");

  const [signUpData, setSignUpData] = useState({
    name: "",
    password: "",
    confirmPassword: "",
    email: "",
    number: ""
  });

  const [signInData, setSignInData] = useState({
    email: "",
    password: ""
  });

  useEffect(() => {
    if (!isAuthenticated && localStorage.getItem("token")) {
      (async () => {
        setLoading(1);
        await userLoaded();
        setLoading(0);
      })();
    } else {
      setLoading(0);
    }
  }, [isAuthenticated, userLoaded]);

  // Handle input changes
  const handleSignUpData = (e) => setSignUpData({ ...signUpData, [e.target.name]: e.target.value });
  const handleSignInData = (e) => setSignInData({ ...signInData, [e.target.name]: e.target.value });

  const togglePassword = () => setInputType(inputType === "password" ? "text" : "password");

  const clickHandle = () => setFlag(prev => prev ^ 1); // toggle sign in/up form

  // Sign In
  const handleSignIn = async (e) => {
    e.preventDefault();
    setLoading(1);
    const success = await login(signInData);
    setLoading(0);
    if (success) {
      loadCart();
      props.history.push("/");
    }
  };

  // Sign Up
  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    if (
      !signUpData.name ||
      !signUpData.password ||
      !signUpData.confirmPassword ||
      !signUpData.email ||
      !signUpData.number
    ) {
      alert("Please fill all fields");
      return;
    }
    if (signUpData.password !== signUpData.confirmPassword) {
      alert("Passwords do not match");
      return;
    }

    setLoading(1);
    const success = await register(signUpData);
    setLoading(0);
    if (success) setFlag(0); // switch to login after successful registration
  };

  return (
    <>
      <Alert />
      {loading === 1 ? (
        <Loader />
      ) : (
        <div className={flag === 0 ? "container1" : "container1 sign-up-mode"}>
          <div className="forms-container" style={{ width: "100%" }}>
            <div className="signin-signup">
              {/* Sign In Form */}
              <form className="sign-in-form" onSubmit={handleSignIn}>
                <h2 className="title">Sign in</h2>
                <div className="input-field">
                  <i className="fas fa-user" />
                  <input
                    name="email"
                    type="email"
                    placeholder="Email"
                    onChange={handleSignInData}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-lock" />
                  <input
                    name="password"
                    type={inputType}
                    placeholder="Password"
                    onChange={handleSignInData}
                  />
                  <i className="fas fa-eye" onClick={togglePassword} style={{ cursor: "pointer" }} />
                </div>
                <input type="submit" value="Login" className="btn solid" />
                <Link to="/forget">Forgot Password?</Link>
              </form>

              {/* Sign Up Form */}
              <form className="sign-up-form" onSubmit={handleSignUpSubmit}>
                <h2 className="title">Sign up</h2>
                <div className="input-field">
                  <i className="fas fa-user" />
                  <input
name="name"
                    type="text"
                    placeholder="Name"
                    value={signUpData.name}
                    onChange={handleSignUpData}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-envelope" />
                  <input
                    name="email"
                    type="email"
                    placeholder="Email"
                    value={signUpData.email}
                    onChange={handleSignUpData}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-phone" />
                  <input
                    name="number"
                    type="text"
                    placeholder="Contact Number"
                    value={signUpData.number}
                    onChange={handleSignUpData}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-lock" />
                  <input
                    name="password"
                    type="password"
                    placeholder="Password"
                    value={signUpData.password}
                    onChange={handleSignUpData}
                  />
                </div>
                <div className="input-field">
                  <i className="fas fa-lock" />
                  <input
                    name="confirmPassword"
                    type="password"
                    placeholder="Confirm Password"
                    value={signUpData.confirmPassword}
                    onChange={handleSignUpData}
                  />
                </div>
                <input type="submit" className="btn" value="Sign up" />
              </form>
            </div>
          </div>

          {/* Panels */}
          <div className="panels-container">
            <div className="panel left-panel">
              <div className="content">
                <h3>New here ?</h3>
                <p>Then Sign Up and Start Ordering!</p>
                <button className="btn transparent" onClick={clickHandle}>Sign up</button>
              </div>
              <img src={require("../images/log.svg")} className="image" alt="" />
            </div>
            <div className="panel right-panel">
              <div className="content">
                <h3>One of us ?</h3>
                <p>Then Sign In and get Started!</p>
                <button className="btn transparent" onClick={clickHandle}>Sign in</button>
              </div>
              <img src={require("../images/register.svg")} className="image" alt="" />
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Auth;
