import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import { useToast } from "../components/GlobalToast";
import API from "../api/axios";
import translations from "../i18n/translations";
import "../styles.css";

const Forget = () => {
  const [mail, setMail] = useState("");
  const history = useHistory();
  const { showToast } = useToast();

  const handleChange = (e) => {
    setMail(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await API.post("api/user/forgot-password", { email: mail });

      // لو الباك رجّع لغة نستعملها
      const lang = res.data.lang || "en";
      const t = translations[lang];

      showToast(res.data.message || t.success, "success");

      setTimeout(() => {
        history.push("/reset-password");
      }, 3000);

    } catch (err) {
      const lang = err.response?.data?.lang || "en";
      const t = translations[lang];
      showToast(err.customMessage|| t.error, "error");
    }
  };

  const t = translations["en"]; // الافتراضي EN

  return (
    <>
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>
        {t.forgotTitle}
      </h4>
      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            {t.forgotDesc}
          </span>
        </p>
        <br />
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label
              style={{
                fontFamily: "Poppins",
                align: "left",
                fontWeight: "550",
              }}
            >
              {t.emailLabel} <MailOutlineIcon />
            </Form.Label>
            <Form.Control
              type="email"
              style={{ width: "125%" }}
              placeholder={t.placeholder}
              onChange={handleChange}
              required
            />
          </Form.Group>

          <Button
            className="btn btn-primary"
            variant="primary"
            type="submit"
            style={{
              borderRadius: "1vh",
              textTransform: "capitalize",
            }}
          >
            {t.btnReset}
          </Button>
        </Form>
      </div>
    </>
  );
};

export default Forget;
