import React, { useState, useContext, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import CartContext from "../context/cart/cartContext";
import LoadingContext from "../context/loading/loadingContext";
import translations from "../i18n/translations";
import "../auth.css"

// Toast بسيط
const Toast = ({ message, type, onClose }) => {
  return (
    <div
      style={{
        position: "fixed",
        top: "10%",
        left: "50%",
        transform: "translateX(-50%)",
        zIndex: 9999,
        backgroundColor: type === "success" ? "darkgreen" : "crimson",
        color: "white",
        padding: "1rem 2rem",
        borderRadius: "1rem",
        fontFamily: "Mulish",
        fontWeight: "bold",
        textAlign: "center",
      }}
    >
      {message}
      <span
        style={{ marginLeft: "1rem", cursor: "pointer" }}
        onClick={onClose}
      >
        ×
      </span>
    </div>
  );
};

const Auth = () => {
  const history = useHistory();
  const { register, login, isAuthenticated, errors } = useContext(AuthContext);
  const { loadCart } = useContext(CartContext);
  const { Loader } = useContext(LoadingContext);

  const [flag, setFlag] = useState(0); // 0 = login, 1 = signup
  const [signUpData, setSignUpData] = useState({
    fullName: "",
    email: "",
    password: "",
    gender: "",
    phone: "",
    passportNumber: "",
  });
  const [signInData, setSignInData] = useState({ email: "", password: "" });
  const [inputType, setInputType] = useState("password");
  const [isLoading, setIsLoading] = useState(false);
  const [showVerification, setShowVerification] = useState(false);
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });
  const [language, setLanguage] = useState("en"); // اللغة الافتراضية

  const t = (key) => translations[language][key] || key;

  useEffect(() => {
    if (isAuthenticated) {
      loadCart();
      history.push("/");
    }
  }, [isAuthenticated, history, loadCart]);

  const handleSignUpChange = (e) =>
    setSignUpData({ ...signUpData, [e.target.name]: e.target.value });

  const handleSignInChange = (e) =>
    setSignInData({ ...signInData, [e.target.name]: e.target.value });

  const togglePassword = () =>
    setInputType(inputType === "password" ? "text" : "password");

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    const { fullName, email, password, gender, phone, passportNumber } = signUpData;
    if (!fullName || !email || !password || !gender || !phone || !passportNumber) {
      setToast({ show: true, message: t("error_fill_all"), type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      await register(signUpData);

      if (errors && errors.length > 0) {
        setToast({ show: true, message: errors[0].msg || t("error_server"), type: "error" });
      } else {
        setToast({ show: true, message: t("success_signup"), type: "success" });
        setShowVerification(true);

        setTimeout(() => {
          setShowVerification(false);
          setFlag(0); // يرجع لصفحة تسجيل الدخول
          setToast({ show: false, message: "", type: "success" });
        }, 10000);
      }
    } catch (err) {
      setToast({
        show: true,
        message: err.response?.data?.message || t("error_server"),
        type: "error",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignInSubmit = async (e) => {
    e.preventDefault();
    const { email, password } = signInData;
    if (!email || !password) {
      setToast({ show: true, message: t("error_fill_all"), type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      await login(signInData);

      if (errors && errors.length > 0) {
        setToast({ show: true, message: errors[0].msg || t("error_wrong_credentials"), type: "error" });
      } else {
        setToast({ show: true, message: t("success_login"), type: "success" });
      }
    } catch (err) {
      setToast({
        show: true,
        message: err.response?.data?.message || t("error_wrong_credentials"),
        type: "error",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const toggleForm = () => setFlag(flag ^ 1);

  return (
    <>
      {isLoading && <Loader />}
      {toast.show && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast({ ...toast, show: false })}
        />
      )}

      <div className={flag === 0 ? "container1" : "container1 sign-up-mode"}>
        <div className="forms-container">
          <div className="signin-signup">
            {/* ===== Sign In Form ===== */}
            <form className="sign-in-form" onSubmit={handleSignInSubmit}>
              <h2 className="title">{t("sign_in")}</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="email"
                  type="email"
                  placeholder={t("email")}
                  value={signInData.email}
                  onChange={handleSignInChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type={inputType}
                  placeholder={t("password")}
                  value={signInData.password}
                  onChange={handleSignInChange}
                />
              </div>
              <input type="submit" value={t("login")} className="btn solid" />
              <Link to="/forget">{t("forgot_password")}</Link>
            </form>

            {/* ===== Sign Up Form ===== */}
            <form className="sign-up-form" onSubmit={handleSignUpSubmit}>
              <h2 className="title">{t("sign_up")}</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="fullName"
                  type="text"
                  placeholder={t("full_name")}
                  value={signUpData.fullName}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-envelope" />
                <input
                  name="email"
                  type="email"
                  placeholder={t("email")}
                  value={signUpData.email}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field select-wrapper">
                <i className="fas fa-venus-mars" />
                <select
                  name="gender"
                  value={signUpData.gender}
                  onChange={handleSignUpChange}
                >
                  <option value="">{t("select_gender")}</option>
                  <option value="male">{t("male")}</option>
                  <option value="female">{t("female")}</option>
                  <option value="other">{t("other")}</option>
                </select>
              </div>
              <div className="input-field">
                <i className="fas fa-phone" />
                <input
                  name="phone"
                  type="text"
                  placeholder={t("phone")}
                  value={signUpData.phone}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-id-card" />
                <input
                  name="passportNumber"
                  type="text"
                  placeholder={t("passport_number")}
                  value={signUpData.passportNumber}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type="password"
                  placeholder={t("password")}
                  value={signUpData.password}onChange={handleSignUpChange}
                />
              </div>
              <input type="submit" className="btn" value={t("sign_up")} />
            </form>
          </div>
        </div>

        {/* ===== Panels ===== */}
        <div className="panels-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>{t("new_here")}</h3>
              <p>{t("signup_prompt")}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {t("sign_up")}
              </button>
            </div>
          </div>
          <div className="panel right-panel">
            <div className="content">
              <h3>{t("one_of_us")}</h3>
              <p>{t("signin_prompt")}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {t("sign_in")}
              </button>
            </div>
          </div>

          {/* قائمة تغيير اللغة */}
          <div
            className="select-wrapper"
            style={{ position: "absolute", top: "1rem", right: "1rem" }}
          >
            <div className="language-selector">
              <select value={language} onChange={(e) => setLanguage(e.target.value)}>
                <option value="en">English</option>
                <option value="ar">العربية</option>
              </select>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Auth;
