const AuthReducer = (state, action) => {
  switch (action.type) {
    case "USER_LOADED":
      return { ...state, isAuthenticated: true, user: action.payload, loading: false };
    case "REGISTER_SUCCESS":
    case "LOGIN_SUCCESS":
      return { ...state, isAuthenticated: true, loading: false };
    case "REGISTER_FAIL":
    case "LOGIN_FAIL":
    case "AUTH_ERROR":
    case "LOGOUT":
      return { ...state, isAuthenticated: false, user: null, errors: action.payload || [] };
    default:
      return state;
  }
};

export default AuthReducer;
