import React, { useContext } from "react";
import AuthContext from "../context/auth/authContext";
import { Route, Redirect } from "react-router-dom";

const PrivateRoutes = ({ component: Component, ...rest }) => {
  const authContext = useContext(AuthContext);

  // حماية من كون الـ context undefined
  if (!authContext) return null;

  const { isAuthenticated, loading, verified } = authContext;

  const NotVerified = () => (
    <div style={{ width: "100%", background: "red", color: "white" }}>
      <center>
        <h1>
          Your Email Id Isn't Verified. Verification Link Will Expire Within an
          hour
        </h1>
      </center>
    </div>
  );

  return (
    <Route
      {...rest}
      render={(props) => {
        if (loading) return null; // أو يمكنك وضع Loader هنا
        if (!isAuthenticated) return <Redirect to="/auth" />;
        if (!verified) return <NotVerified />; // عرض رسالة لو الإيميل غير مفعل
        return <Component {...props} />;
      }}
    />
  );
};

export default PrivateRoutes;
