import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import { useToast } from "../components/GlobalToast";
import API from "../api/axios";
import translations from "../i18n/translations";
import "../styles.css";
import { useLanguage } from "../context/LanguageContext";
import axios from "axios";

const Forget = () => {
  const { t, lang } = useLanguage();
  const [mail, setMail] = useState("");
  const history = useHistory();
  const { showToast } = useToast();
    const [isLoading, setIsLoading] = useState(false);
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });
  const handleChange = (e) => {
    setMail(e.target.value);
  };

    const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      setToast({ show: true, message: t("error_fill_all"), type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          lang: lang,
        },
      };

      const res = await axios.post(
        "http://localhost:5000/api/user/forgot-password",
        { email },
        config
      );

      setToast({ show: true, message: res.data.message, type: "success" });
    } catch (err) {
      const data = err.response?.data;
      if (data?.errors?.length > 0) {
        setToast({ show: true, message: data.errors[0].message, type: "error" });
      } else if (data?.message) {
        setToast({ show: true, message: data.message, type: "error" });
      } else {
        setToast({ show: true, message: t("error_server"), type: "error" });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>
        {t.forgotTitle}
      </h4>
      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            {t.forgotDesc}
          </span>
        </p>
        <br />
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label
              style={{
                fontFamily: "Poppins",
                align: "left",
                fontWeight: "550",
              }}
            >
              {t.emailLabel} <MailOutlineIcon />
            </Form.Label>
            <Form.Control
              type="email"
              style={{ width: "125%" }}
              placeholder={t.placeholder}
              onChange={handleChange}
              required
            />
          </Form.Group>

          <Button
            className="btn btn-primary"
            variant="primary"
            type="submit"
            style={{
              borderRadius: "1vh",
              textTransform: "capitalize",
            }}
          >
            {t.btnReset}
          </Button>
        </Form>
      </div>
    </>
  );
};

export default Forget;
