import React, { useReducer, createContext } from "react";
import AuthReducer from "./authReducer";
import axios from "axios";

export const AuthContext = createContext();

const AuthState = (props) => {
  const initialState = {
    isAuthenticated: false,
    user: null,
    loading: false,
    errors: [],
    displayVerified: false, // لإظهار مربع الإشعار بعد التسجيل
  };

  const [state, dispatch] = useReducer(AuthReducer, initialState);

  // تسجيل حساب جديد
  const register = async (formData) => {
    try {
      const res = await axios.post("http://localhost:5000/api/user/auth/register", formData);

      // إذا الحساب غير موثق بعد
      if (!res.data.isAccountVerified) {
        dispatch({ type: "DISPLAY_VERIFIED", payload: true });
      } else {
        dispatch({ type: "REGISTER_SUCCESS", payload: res.data });
      }
    } catch (err) {
      dispatch({ type: "REGISTER_FAIL", payload: err.response?.data });
    }
  };

  // تسجيل دخول
  const login = async (formData) => {
    try {
      const res = await axios.post("http://localhost:5000/api/user/auth/login", formData);
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
    } catch (err) {
      dispatch({ type: "LOGIN_FAIL", payload: err.response?.data });
    }
  };

  return (
    <AuthContext.Provider
      value={{
        register,
        login,
        isAuthenticated: state.isAuthenticated,
        displayVerified: state.displayVerified,
        errors: state.errors,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthState;
