import React, { useState } from "react";
import { Form, Button, Toast } from "react-bootstrap";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import "../styles.css";
import axios from "axios";

const Forget = () => {
  const [mail, setMail] = useState("");
  const [status, setStatus] = useState({ val: 0, msg: "", type: "" });

  const handleChange = (e) => setMail(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!mail) return alert("Please enter your email");

    try {
      const res = await axios.post("https://foodeazy.herokuapp.com/forget", { email: mail });
      setStatus({ val: 1, msg: "Code sent to your email!", type: "darkGreen" });

      setTimeout(() => setStatus({ val: 0, msg: "", type: "" }), 3000);
    } catch (e) {
      setStatus({ val: 1, msg: e.response?.data || "Error occurred", type: "red" });
      setTimeout(() => setStatus({ val: 0, msg: "", type: "" }), 3000);
    }
  };

  const Alert = () => (
    <Toast style={{ position: "fixed", right: "0", zIndex: "9899899" }}>
      <Toast.Header
        style={{
          background: status.type === "darkGreen" ? "green" : "red",
          color: "white",
          fontWeight: "bold"
        }}
      >
        <strong className="mr-auto">
          {status.type === "darkGreen" ? "Success" : "Fail"}
        </strong>
      </Toast.Header>
      <Toast.Body>{status.msg}</Toast.Body>
    </Toast>
  );

  return (
    <div className="forget">
      {status.val ? <Alert /> : null}
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>Forgot your Password?</h4>
      <p style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
        Enter your email address and we'll send you a code to reset your password.
      </p>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email Address <MailOutlineIcon /></Form.Label>
          <Form.Control
            type="email"
            placeholder="e.g. email@domain.com"
            value={mail}
            onChange={handleChange}
            style={{ width: "125%" }}
          />
        </Form.Group>
        <Button type="submit" className="btn btn-primary" style={{ borderRadius: "1vh", textTransform: "capitalize" }}>
          Send Code
        </Button>
      </Form>
    </div>
  );
};

export default Forget;
