import React, { useReducer, createContext, useEffect, useRef  } from "react";
import AuthReducer from "./authReducer";
import axios from "axios";
import setAuthToken from "./setAuthToken";
import AuthContext from "./authContext";

const AuthState = (props) => {
  const initialState = {
    isAuthenticated: false,
    user: null,
    loading: false,
    errors: [],
    current: 0,
    verified: false,
    displayVerified: false,
  };

  const [state, dispatch] = useReducer(AuthReducer, initialState);

  // Ref لمعرفة إذا المكون مازال موجود
  const isMounted = useRef(true);
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  const userLoaded = async () => {
    if (localStorage.token) setAuthToken(localStorage.token);
    try {
      const config = { headers: { "Content-Type": "application/json" } };
      const res = await axios.get("http://localhost:5000/api/user/current-user", config);
      if (isMounted.current) dispatch({ type: "USER_LOADED", payload: res.data });
    } catch (err) {
      if (isMounted.current) dispatch({ type: "AUTH_ERROR" });
    }
  };

  const register = async (formData) => {
    const config = { headers: { "Content-Type": "application/json" } };
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/auth/register",
        formData,
        config
      );
      localStorage.setItem("token", res.data.token);
      if (isMounted.current) {
        dispatch({ type: "REGISTER_SUCCESS", payload: res.data });
        userLoaded();
      }
    } catch (err) {
      if (isMounted.current) dispatch({ type: "REGISTER_FAIL", payload: err.response?.data });
    }
  };

  const login = async (formData) => {
    const config = { headers: { "Content-Type": "application/json" } };
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/auth/login",
        formData,
        config
      );
      localStorage.setItem("token", res.data.token);
      if (isMounted.current) {
        dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
        userLoaded();
      }
    } catch (err) {
      if (isMounted.current) dispatch({ type: "LOGIN_FAIL", payload: err.response?.data });
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    setAuthToken(null);
    if (isMounted.current) dispatch({ type: "LOGOUT" });
  };

  return (
    <AuthContext.Provider
      value={{
        register,
        login,
        logout,
        userLoaded,
        isAuthenticated: state.isAuthenticated,
        user: state.user,
        loading: state.loading,
        errors: state.errors,
        current: state.current,
        verified: state.verified,
        displayVerified: state.displayVerified,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthState;
