import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import API from "../api/axios";
import translations from "../i18n/translations";
import Toast from "./toastAnimated";
import "../styles.css";

const ResetPassword = () => {
  const history = useHistory();

  // Toast state (ŸÜŸÅÿ≥ ÿßŸÑÿ¥Ÿä ŸäŸÑŸä ŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ®ÿßŸÑÿ£Ÿàÿ´)
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });

  // Form data state
  const [formData, setFormData] = useState({
    email: "",
    resetCode: "",
    newPassword: "",
  });

  const [isLoading, setIsLoading] = useState(false); // Loader state

  // Handle input change
  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // Handle submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    const { email, resetCode, newPassword } = formData;
    if (!email || !resetCode || !newPassword) {
      setToast({ show: true, message: translations.en.error_fill_all, type: "error" });
      return;
    }

    setIsLoading(true);

    try {
      // ŸÜÿ±ÿ≥ŸÑ ÿßŸÑŸÑÿ∫ÿ© ŸÅŸä ÿßŸÑŸáŸäÿØÿ± ŸÖÿ´ŸÑ ÿßŸÑÿ£Ÿàÿ´
      const lang = localStorage.getItem("lang") || "en";

      const res = await API.post(
        "/user/reset-password",
        formData,
        { headers: { lang } }
      );

      // ÿπÿ±ÿ∂ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ®ÿßŸÉ ÿ≠ÿ≥ÿ® ÿßŸÑŸÑÿ∫ÿ©
      setToast({
        show: true,
        message: res.data?.message || translations[lang].success,
        type: "success",
      });

      // ÿ®ÿπÿØ Ÿ£ ÿ´ŸàÿßŸÜŸä ŸäŸÜÿ™ŸÇŸÑ ŸÑÿµŸÅÿ≠ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
      setTimeout(() => {
        history.push("/auth");
      }, 3000);

    } catch (err) {
      const lang = err.response?.data?.lang || localStorage.getItem("lang") || "en";
      const msg = err.response?.data?.message || translations[lang].error;
      setToast({ show: true, message: msg, type: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  const lang = localStorage.getItem("lang") || "en";
  const t = translations[lang];

   return (
    <div className="reset-password-container" style={{ marginTop: "5vh" }}>
      {/* üè∑Ô∏è Page title (centered) */}
      <h3 style={{ textAlign: "center", fontFamily: "Mulish" }}>
        {tr("reset_password", "Reset Your Password")}
      </h3>

      {/* üîê Form */}
      <Form onSubmit={handleSubmit} style={{ maxWidth: "400px", margin: "auto" }}>
        {/* Email */}
        <Form.Group>
          <Form.Label>
            {tr("email", "Email")} <MailOutlineIcon />
          </Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder={tr("placeholder_email", "e.g. email@domain.com")}
            required
          />
        </Form.Group>

        {/* Verification Code */}
        <Form.Group>
          <Form.Label>
            {tr("verification_code", "Verification Code")} <LockIcon />
          </Form.Label>
          <Form.Control
            type="text"
            name="resetCode"
            value={formData.resetCode}
            onChange={handleChange}
            placeholder={tr("placeholder_code", "Enter the code you received")}
            required
          />
        </Form.Group>

        {/* New Password */}
        <Form.Group>
          <Form.Label>
            {tr("new_password", "New Password")} <LockIcon />
          </Form.Label><Form.Control
            type="password"
            name="newPassword"
            value={formData.newPassword}
            onChange={handleChange}
            placeholder={tr("placeholder_new_password", "Enter new password")}
            required
          />
        </Form.Group>

        {/* Submit */}
        <Button
          type="submit"
          style={{
            width: "100%",
            borderRadius: "1rem",
            marginTop: "1rem",
            backgroundColor: "#007bff",
            border: "none",
          }}
        >
          {tr("reset_password", "Reset Password")}
        </Button>
      </Form>
    </div>
  );
};

export default ResetPassword;
