import React, { useState } from "react";
import { useLanguage } from "../context/LanguageContext";
import axios from "axios";

import "../styles.css";
import Toast from "./toastAnimated";

const ForgotPassword = () => {
  const { t, lang } = useLanguage();
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      setToast({ show: true, message: t("error_fill_all"), type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          lang: lang,
        },
      };

      const res = await axios.post(
        "http://localhost:5000/api/user/forgot-password",
        { email },
        config
      );

      setToast({ show: true, message: res.data.message, type: "success" });
    } catch (err) {
      const data = err.response?.data;
      if (data?.errors?.length > 0) {
        setToast({ show: true, message: data.errors[0].message, type: "error" });
      } else if (data?.message) {
        setToast({ show: true, message: data.message, type: "error" });
      } else {
        setToast({ show: true, message: t("error_server"), type: "error" });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="forgot-password-container">
      {isLoading && <div className="loader"></div>}

      {toast.show && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast({ ...toast, show: false })}
        />
      )}

      <h2 className="title">{t("reset_password")}</h2>
      <p>{t("forgot_password")}</p>

      <form onSubmit={handleSubmit}>
        <div className="">
          <input
            type="email"
            placeholder={t("email")}
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <button type="submit" className="btn">
          {t("send_code")}
        </button>
      </form>
    </div>
  );
};

export default ForgotPassword;
