import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import "../styles.css";
import axios from "axios";
import { useHistory } from "react-router-dom";

const Forget = () => {
  const [mail, setMail] = useState("");
  const [showNotif, setShowNotif] = useState(false);
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!mail) return alert("Please enter your email");

    try {
      await axios.post("http://localhost:5000/api/user/forgot-password", { email: mail });

      // إظهار الإشعار
      setShowNotif(true);

      // بعد 3 ثواني انتقل لصفحة ResetPassword
      setTimeout(() => {
        setShowNotif(false);
        history.push("/reset-password");
      }, 2000);
    } catch (e) {
      alert(e.response?.data || "Error occurred");
    }
  };

  return (
    <div className="forget">
      {showNotif && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            background: "white",
            padding: "2rem 3rem",
            borderRadius: "1rem",
            boxShadow: "0px 4px 15px rgba(0,0,0,0.2)",
            textAlign: "center",
            zIndex: 9999,
            fontFamily: "Mulish",
            animation: "fadeIn 0.5s"
          }}
        >
          <h4>Check your email!</h4>
          <p>We have sent a verification code to your inbox.</p>
        </div>
      )}

      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>Forgot your Password?</h4>
      <p style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
        Enter your email address and we'll send you a code to reset your password.
      </p>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email Address <MailOutlineIcon /></Form.Label>
          <Form.Control
            type="email"
            placeholder="Your email"
            value={mail}
            onChange={(e) => setMail(e.target.value)}
            style={{ width: "125%" }}
          />
        </Form.Group>
        <Button type="submit" className="btn btn-primary" style={{ borderRadius: "1vh", textTransform: "capitalize" }}>
          Send Code
        </Button>
      </Form>
    </div>
  );
};

export default Forget;
