import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { Navbar, Nav } from "react-bootstrap";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import AuthContext from "../context/auth/authContext";
import LanguageContext from "../context/language/LanguageContext";
import "../styles.css";

export default function Navbar1(props) {
  const authContext = useContext(AuthContext);
  const { language, setLanguage, t } = useContext(LanguageContext);

  if (!authContext) return null;

  const { logout, isAuthenticated } = authContext;

  const handleLogout = () => {
    logout();
    window.location.href = "/auth";
  };

  return (
    <Navbar
      style={{
        position: "sticky",
        top: "0",
        zIndex: "100000",
        display: "flex",
        justifyContent: "space-between",
        fontFamily: "Mulish",
        backgroundColor: "#d0f0fd", // أزرق فاتح باهت
      }}
      variant="light"
    >
      <Navbar.Brand className="navbar titl">
        <Link to="/" style={{ color: "#0077b6", fontFamily: "Mulish" }}>
          Syrian Flight <i className="fas fa-utensils" />
        </Link>
      </Navbar.Brand>

      <Nav style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
        <Link to="/" style={{ color: "#0077b6", textDecoration: "none" }}>
          {t("home") || "Home"}
        </Link>

        {isAuthenticated && (
          <>
            <Link to="/order" style={{ color: "#0077b6", textDecoration: "none" }}>
              {t("orders") || "Orders"}
            </Link>
            <Link to="/addproduct" style={{ color: "#0077b6", textDecoration: "none" }}>
              {t("add_items") || "Add Items"}
            </Link>
            <Link to="/deleteitems" style={{ color: "#0077b6", textDecoration: "none" }}>
              {t("delete_items") || "Delete Items"}
            </Link>
            <Link to="/contact" style={{ color: "#0077b6", textDecoration: "none" }}>
              {t("contact_us") || "Contact Us"}
            </Link>

            <button
              type="button"
              onClick={handleLogout}
              style={{
                border: "none",
                outline: "none",
                color: "#0077b6",
                background: "transparent",
                cursor: "pointer",
              }}
            >
              {t("logout")} <ExitToAppIcon />
            </button>
          </>
        )}

        {/* قائمة اختيار اللغة */}
        <select
          value={language}
          onChange={(e) => setLanguage(e.target.value)}
          style={{
            padding: "5px 10px",
            borderRadius: "5px",
            border: "1px solid #0077b6",
            cursor: "pointer",
            fontFamily: "Mulish",
          }}
        >
          <option value="en">English</option>
          <option value="ar">العربية</option>
        </select>
      </Nav>
    </Navbar>
  );
}
