import React, { useState } from "react";
import { Form, Toast, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from '@material-ui/icons/MailOutline';
import { useHistory } from "react-router-dom";
import axios from "axios";
import "../styles.css";

const Forget = () => {
  const [mail, setMail] = useState("");
  const [status, setStatus] = useState({ val: 0, msg: "", type: "" });
  const history = useHistory();

  const Alert = () => {
    return (
      <Toast 
        style={{
          position: "fixed",
          top: "20vh",
          left: "50%",
          transform: "translateX(-50%)",
          zIndex: "999999",
          minWidth: "300px",
          borderRadius: "10px"
        }}
        className={status.type === "darkGreen" ? "bg-success text-white" : "bg-danger text-white"}
      >
        <Toast.Body style={{ textAlign: "center", fontWeight: "bold" }}>
          {status.msg}
        </Toast.Body>
      </Toast>
    );
  };

  const handleChange = (e) => setMail(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!mail) return alert("Please enter your email");
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/forgot-password",
        { email: mail },
        { headers: { "Content-Type": "application/json" } }
      );
      setStatus({ val: 1, msg: res.data, type: "darkGreen" });

      // بعد 3 ثواني الانتقال لصفحة إدخال الرمز وكلمة المرور الجديدة
      setTimeout(() => {
        history.push(`/reset-password/${mail}`);
      }, 3000);

    } catch (e) {
      setStatus({ val: 1, msg: e.response?.data || "Error", type: "red" });
      setTimeout(() => setStatus({ val: 0, msg: "", type: "" }), 3000);
    }
  };

  return (
    <>
      {status.val && <Alert />}
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>Forgot your Password?</h4>
      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            Enter your email address and we'll send you a link or code to reset your password.
          </span>
        </p>

        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label style={{ fontFamily: "Poppins", fontWeight: "550" }}>
              Email Address <MailOutlineIcon />
            </Form.Label>
            <Form.Control
              type="email"
              style={{ borderRadius: "10px", padding: "1vh", fontSize: "0.95rem" }}
              placeholder="e.g. email@domain.com"
              value={mail}
              onChange={handleChange}
            />
          </Form.Group>

          <Button
            className="btn btn-primary"
            variant="primary"
            type="submit"
            style={{ borderRadius: "1vh", textTransform: "capitalize" }}
          >
            Reset Password
          </Button>
        </Form>
      </div>
    </>
  );
};

export default Forget;
