import React, { useState, useContext, useEffect, useRef } from "react";
import { Link, useHistory } from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import CartContext from "../context/cart/cartContext";
import LoadingContext from "../context/loading/loadingContext";
import "../auth.css";

// Toast بسيط
const Toast = ({ message, type, onClose }) => {
  return (
<div className="input-field">
                <i className="fas fa-venus-mars" />
      {message}
      <span
        style={{ marginLeft: "1rem", cursor: "pointer" }}
        onClick={onClose}
      >
        ×
      </span>
    </div>
  );
};

const Auth = () => {
  const history = useHistory();
  const { register, login, isAuthenticated, errors } = useContext(AuthContext);
  const { loadCart } = useContext(CartContext);
  const { Loader } = useContext(LoadingContext);

  const isMounted = useRef(true);
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  const [flag, setFlag] = useState(0); // 0 = login, 1 = signup
  const [signUpData, setSignUpData] = useState({
    fullName: "",
    email: "",
    password: "",
    gender: "",
    phone: "",
    passportNumber: "",
  });
  const [signInData, setSignInData] = useState({ email: "", password: "" });
  const [inputType, setInputType] = useState("password");
  const [isLoading, setIsLoading] = useState(false);
  const [showVerification, setShowVerification] = useState(false);
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });
  const [language, setLanguage] = useState("en"); // اللغة الافتراضية

  useEffect(() => {
    if (isAuthenticated) {
      loadCart();
      history.push("/");
    }
  }, [isAuthenticated, history, loadCart]);

  const handleSignUpChange = (e) =>
    setSignUpData({ ...signUpData, [e.target.name]: e.target.value });

  const handleSignInChange = (e) =>
    setSignInData({ ...signInData, [e.target.name]: e.target.value });

  const togglePassword = () =>
    setInputType(inputType === "password" ? "text" : "password");

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    const { fullName, email, password, gender, phone, passportNumber } = signUpData;
    if (!fullName || !email || !password || !gender || !phone || !passportNumber) {
      setToast({ show: true, message: "Please fill all fields", type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      await register(signUpData);

      // اظهار اشعار تحقق البريد
      setToast({
        show: true,
        message:
          language === "ar"
            ? "تم إنشاء الحساب! تحقق من بريدك الإلكتروني لتأكيد الحساب."
            : "Account created! Check your email to verify your account.",
        type: "success",
      });
      setShowVerification(true);

      setTimeout(() => {
        setShowVerification(false);
        setFlag(0); // يرجع لصفحة تسجيل الدخول
        setToast({ show: false, message: "", type: "success" });
      }, 10000);
    } catch (err) {
      setToast({ show: true, message: err.response?.data?.message || "Error", type: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignInSubmit = async (e) => {
    e.preventDefault();
    const { email, password } = signInData;
    if (!email || !password) {
      setToast({ show: true, message: "Please fill all fields", type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      await login(signInData);
    } catch (err) {
      setToast({ show: true, message: err.response?.data?.message || "Error", type: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  const toggleForm = () => setFlag(flag ^ 1);

  return (
    <>
      {isLoading && <Loader />}
      {toast.show && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast({ ...toast, show: false })}
        />
      )}

      <div className={flag === 0 ? "container1" : "container1 sign-up-mode"}>
        <div className="forms-container">
          <div className="signin-signup">
            {/* ===== Sign In Form ===== */}
            <form className="sign-in-form" onSubmit={handleSignInSubmit}>
              <h2 className="title">{language === "ar" ? "تسجيل الدخول" : "Sign in"}</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="email"
                  type="email"
                  placeholder={language === "ar" ? "البريد الإلكتروني" : "Email"}
                  value={signInData.email}
                  onChange={handleSignInChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type={inputType}
                  placeholder={language === "ar" ? "كلمة المرور" : "Password"}
                  value={signInData.password}
                  onChange={handleSignInChange}
                />
                <button type="button" onClick={togglePassword}>
                  {inputType === "password" ? "Show" : "Hide"}
                </button>
              </div>
              <input type="submit" value={language === "ar" ? "تسجيل الدخول" : "Login"} className="btn solid" />
              <Link to="/forget">{language === "ar" ? "نسيت كلمة المرور؟" : "Forgot Password?"}</Link>
            </form>

            {/* ===== Sign Up Form ===== */}
            <form className="sign-up-form" onSubmit={handleSignUpSubmit}>
              <h2 className="title">{language === "ar" ? "تسجيل حساب جديد" : "Sign up"}</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="fullName"
                  type="text"
                  placeholder={language === "ar" ? "الاسم الكامل" : "Full Name"}
                  value={signUpData.fullName}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-envelope" />
                <input
                  name="email"
                  type="email"
                  placeholder={language === "ar" ? "البريد الإلكتروني" : "Email"}
                  value={signUpData.email}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-venus-mars" />
                <select
                  name="gender"
                  value={signUpData.gender}
                  onChange={handleSignUpChange}
                >
                  <option value="">{language === "ar" ? "اختر الجنس" : "Select Gender"}</option>
                  <option value="male">{language === "ar" ? "ذكر" : "Male"}</option>
                  <option value="female">{language === "ar" ? "أنثى" : "Female"}</option>
                  <option value="other">{language === "ar" ? "آخر" : "Other"}</option>
                </select>
              </div>
              <div className="input-field">
                <i className="fas fa-phone" />
                <input
                  name="phone"
                  type="text"
                  placeholder={language === "ar" ? "رقم الهاتف" : "Phone"}
                  value={signUpData.phone}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-id-card" />
                <input
                  name="passportNumber"
                  type="text"
                  placeholder={language === "ar" ? "رقم الجواز" : "Passport Number"}
                  value={signUpData.passportNumber}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type="password"
                  placeholder={language === "ar" ? "كلمة المرور" : "Password"}
                  value={signUpData.password}
                  onChange={handleSignUpChange}
                />
              </div>
              <input type="submit" className="btn" value={language === "ar" ? "تسجيل" : "Sign up"} />
            </form>
          </div>
        </div>

        {/* ===== Panels ===== */}
        <div className="panels-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>{language === "ar" ? "جديد هنا؟" : "New here?"}</h3>
              <p>{language === "ar" ? "سجل حساب وابدأ الطلب!" : "Then Sign Up and Start Ordering!"}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {language === "ar" ? "تسجيل" : "Sign up"}
              </button>
            </div>
          </div>
          <div className="panel right-panel">
            <div className="content">
              <h3>{language === "ar" ? "لديك حساب؟" : "One of us?"}</h3>
              <p>{language === "ar" ? "سجل الدخول وابدأ!" : "Then Sign In and get Started!"}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {language === "ar" ? "تسجيل الدخول" : "Sign in"}
              </button>
            </div>
          </div>

          {/* قائمة تغيير اللغة */}
          <div className="language-selector" style={{ position: "absolute", top: "1rem", right: "1rem" }}>
            <select value={language} onChange={(e) => setLanguage(e.target.value)}>
              <option value="en">English</option>
              <option value="ar">العربية</option>
            </select>
          </div>
        </div>
      </div>
    </>
  );
};

export default Auth;
