import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import { useToast } from "../components/GlobalToast";
import API from "../api/axios";
import translations from "../i18n/translations";
import "../styles.css";
import { useLanguage } from "../context/LanguageContext";
import axios from "axios";

const Forget = () => {
  const { t, lang } = useLanguage();
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });
  const handleChange = (e) => {
    setMail(e.target.value);
  };

    const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      setToast({ show: true, message: t("error_fill_all"), type: "error" });
      return;
    }

    setIsLoading(true);
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          lang: lang,
        },
      };

      const res = await axios.post(
        "http://localhost:5000/api/user/forgot-password",
        { email },
        config
      );

      setToast({ show: true, message: res.data.message, type: "success" });
    } catch (err) {
      const data = err.response?.data;
      if (data?.errors?.length > 0) {
        setToast({ show: true, message: data.errors[0].message, type: "error" });
      } else if (data?.message) {
        setToast({ show: true, message: data.message, type: "error" });
      } else {
        setToast({ show: true, message: t("error_server"), type: "error" });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <div className="forgot-password-container">
      {isLoading && <div className="loader"></div>}
         {toast.show && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast({ ...toast, show: false })}
        />
      )}
       <h2 className="title">{t("reset_password")}</h2>
      <p>{t("forgot_password")}</p>
      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            {t("forgotDesc")}
          </span>
        </p>
        <br />
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label style={{fontFamily: "Poppins",align: "center",fontWeight: "550",}}>
              {t("emailLabel")} <MailOutlineIcon />
            </Form.Label>
            <Form.Control type="email"style={{ width: "150%" }}placeholder={t("placeholder")}onChange={handleChange}required/>
          </Form.Group>

                 <button type="submit" className="btn">
          {t("send_code")}
        </button>
        </Form>
      </div>
       </div>
    </>
  );
};

export default Forget;
