import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import Toast from "./toastAnimated";
import axios from "axios";
import { useLanguage } from "../context/LanguageContext";
import "../styles.css";

const Forget = () => {
  const { t, lang } = useLanguage(); // استدعاء الترجمة واللغة الحالية
  const [mail, setMail] = useState("");
  const [status, setStatus] = useState({ show: false, msg: "", type: "" });
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });
  const handleChange = (e) => setMail(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!mail) {
      setStatus({
        show: true,
        msg: t("error_fill_all"),
        type: "red"
      });
      hideToast();
      return;
    }

    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          lang: lang // إرسال اللغة إلى الباك
        }
      };

      const res = await axios.post(
        "http://localhost:5000/api/user/forgot-password",
        { email: mail },
        config
      );

      setStatus({
        show: true,
        msg: res.data?.message || t("success_check_email"),
        type: "darkGreen"
      });
      hideToast();
    } catch (e) {
      const data = e.response?.data;
      setStatus({
        show: true,
        msg: data?.errors?.[0]?.message || data?.message || t("error_server"),
        type: "red"
      });
      hideToast();
    }
  };

  const hideToast = () => {
    let id = setTimeout(() => {
      setStatus({ show: false, msg: "", type: "" });
      clearTimeout(id);
    }, 3000);
  };

  return (
    <>
      {toast.show && (
              <Toast
                show={toast.show}
                message={toast.message}
                type={toast.type}
                onClose={() => setToast({ ...toast, show: false })}
              />
            )}
      
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" ,textAlign:"center"}}>
        {t("forgot_password_title")}
      </h4>

      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />{" "}
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            {t("forgot_password_description")}
          </span>
        </p>

        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label
              style={{
                fontFamily: "Poppins",
                fontWeight: "550"
              }}
            >
              {t("email_label")} <MailOutlineIcon />
            </Form.Label>
            <Form.Control
              type="email"
              style={{ width: "125%" }}
              placeholder={t("email_placeholder")}
              value={mail}
              onChange={handleChange}
            />
          </Form.Group>

          <Button
            className="btn btn-primary"
            variant="primary"
            type="submit"
            style={{
              borderRadius: "4vh",
              textTransform: "capitalize",
              width: "15%" 
            }}
          >
            {t("reset_password")}
          </Button>
        </Form>
      </div>
    </>
  );
};

export default Forget;
