export default (state, action) => {
  switch (action.type) {
    case "USER_LOADED":
      return { ...state, isAuthenticated: true, loading: false, user: action.payload };

    case "REGISTER_SUCCESS":
    case "LOGIN_SUCCESS":
      return { ...state, isAuthenticated: true, loading: false, user: action.payload, errors: [] };

    case "REGISTER_FAIL":
    case "LOGIN_FAIL":
    case "AUTH_ERROR":
    case "LOGOUT":
      return { ...state, isAuthenticated: false, loading: false, user: null, errors: action.payload ? [action.payload] : [] };

    case "SET_LOADING":
      return { ...state, loading: true };

    case "REMOVE_ALERT":
      return { ...state, errors: [] };

    case "REMOVE_CURRENT":
      return { ...state, current: 0 };

    case "SET_SUCCESS_MESSAGE":
      return { ...state, successMessage: action.payload };

    case "REMOVE_DISPLAY_VERIFIED":
      return { ...state, displayVerified: false };

    default:
      return state;
  }
};
