import React from "react";
import { Toast } from "react-bootstrap";

const GlobalToast = ({ show, onClose, type = "success", children }) => {
  const bg = type === "success" ? "#198754" : type === "warning" ? "#f0ad4e" : "#dc3545";
  return (
  <Toast
    style={{
      position: "fixed",
      top: "20px",
      right: "20px",
      minWidth: "250px",
      zIndex: 99999,   // أهم شي يطلع فوق الـ Navbar
    }}
    onClose={() => setStatus({ val: 0, msg: "", type: "" })}
    show={true}
    delay={3000}
    autohide
  >
    <Toast.Header
      style={{
        backgroundColor: status.type === "darkGreen" ? "green" : "red",
        color: "white",
        fontWeight: "bold",
      }}
    >
      <strong className="mr-auto">
        {status.type === "darkGreen" ? "Success" : "Error"}
      </strong>
    </Toast.Header>
    <Toast.Body>{status.msg}</Toast.Body>
  </Toast>
)
};

export default GlobalToast;
