import React, { useState, useContext, useEffect, useRef } from "react";
import AuthContext from "../context/auth/authContext";

const Auth = () => {
  const { register, login, errors, isAuthenticated } = useContext(AuthContext);

  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    password: "",
    gender: "",
    phone: "",
    passportNumber: "",
  });

  const [isLogin, setIsLogin] = useState(true); // لتبديل بين تسجيل الدخول والتسجيل

  const isMounted = useRef(true);

  useEffect(() => {
    return () => {
      isMounted.current = false; // منع أي تحديث بعد فك المكون
    };
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLogin) {
      try {
        await login({
          email: formData.email,
          password: formData.password,
        });
        // يمكن إضافة أي تحديث state بعد تسجيل الدخول هنا
      } catch (err) {
        console.log(err);
      }
    } else {
      try {
        await register(formData);
        // يمكن إضافة أي تحديث state بعد التسجيل هنا
      } catch (err) {
        console.log(err);
      }
    }
  };

  return (
    <div className="auth-container">
      <h2>{isLogin ? "Login" : "Register"}</h2>
      <form onSubmit={handleSubmit}>
        {!isLogin && (
          <>
            <input
              type="text"
              name="fullName"
              placeholder="Full Name"
              value={formData.fullName}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="gender"
              placeholder="Gender"
              value={formData.gender}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="phone"
              placeholder="Phone Number"
              value={formData.phone}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="passportNumber"
              placeholder="Passport Number"
              value={formData.passportNumber}
              onChange={handleChange}
              required
            />
          </>
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit">{isLogin ? "Login" : "Register"}</button>
      </form>
      <p
        onClick={() => setIsLogin(!isLogin)}
        style={{ cursor: "pointer", color: "blue", marginTop: "10px" }}
      >
        {isLogin
          ? "Don't have an account? Register here"
          : "Already have an account? Login here"}
      </p>
      {errors && errors.length > 0 && (
        <div className="auth-errors">
          {errors.map((err, idx) => (
            <p key={idx} style={{ color: "red" }}>
              {err.msg || err}
            </p>
          ))}
        </div>
      )}
    </div>
  );
};

export default Auth;
