import React, { useState, useMemo } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import { useToast } from "../components/GlobalToast";
import API from "../api/axios";              
import translations from "../i18n/translations"; 
import { useLanguage } from "../context/LanguageContext"; 
import "../styles.css";

const ResetPassword = () => {
  const history = useHistory();
  const { showToast } = useToast();

  // üî§ current UI language from context (fallback to 'en' if missing)
  const { lang = "en" } = useLanguage?.() || { lang: "en" };

  // üîé tiny helper to safely read translations with fallback to EN or custom default
  const tr = useMemo(
    () => (key, fallback) =>
      (translations?.[lang] && translations[lang][key]) ||
      (translations?.en && translations.en[key]) ||
      fallback ||
      key,
    [lang]
  );

  // üó≥Ô∏è Local form state
  const [formData, setFormData] = useState({
    email: "",
    resetCode: "",
    newPassword: "",
  });

  // üß≠ Controlled inputs change
  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // üì® Submit: validate, call backend, toast, then redirect
  const handleSubmit = async (e) => {
    e.preventDefault();

    const { email, resetCode, newPassword } = formData;

    // üõë Frontend validation
    if (!email || !resetCode || !newPassword) {
      showToast(tr("error_fill_all", "Please fill all fields"), "error");
      return;
    }

    try {
      // üåê Send current language to backend so it returns localized messages
      const config = { headers: { "Content-Type": "application/json", lang } };

      // üì° Backend call
      const res = await API.post("/user/reset-password", formData, config);

      // ‚úÖ Prefer backend message; fallback to our dictionary
      const okMsg =
        res?.data?.message ||
        tr("success_reset_password", "Password reset successfully");

      showToast(okMsg, "success");

      // ‚è© Redirect to login after ~1.5s so the user sees the toast
      setTimeout(() => history.push("/auth"), 1500);
    } catch (err) {
      // ‚ùå Robust backend error extraction (supports multiple shapes)
      const data = err?.response?.data;
      const serverMsg =
        (data && (data.errors?.[0]?.message || data.message)) 
        err?.message ||
        tr("error_server", "Server error, please try again");

      showToast(serverMsg, "error");
    }
  };

  return (
    <div className="reset-password-container" style={{ marginTop: "5vh" }}>
      {/* üè∑Ô∏è Page title (centered) */}
      <h3 style={{ textAlign: "center", fontFamily: "Mulish" }}>
        {tr("reset_password", "Reset Your Password")}
      </h3>

      {/* üîê Form */}
      <Form onSubmit={handleSubmit} style={{ maxWidth: "400px", margin: "auto" }}>
        {/* Email */}
        <Form.Group>
          <Form.Label>
            {tr("email", "Email")} <MailOutlineIcon />
          </Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder={tr("placeholder_email", "e.g. email@domain.com")}
            required
          />
        </Form.Group>

        {/* Verification Code */}
        <Form.Group>
          <Form.Label>
            {tr("verification_code", "Verification Code")} <LockIcon />
          </Form.Label>
          <Form.Control
            type="text"
            name="resetCode"
            value={formData.resetCode}
            onChange={handleChange}
            placeholder={tr("placeholder_code", "Enter the code you received")}
            required
          />
        </Form.Group>

        {/* New Password */}
        <Form.Group>
          <Form.Label>
            {tr("new_password", "New Password")} <LockIcon />
          </Form.Label><Form.Control
            type="password"
            name="newPassword"
            value={formData.newPassword}
            onChange={handleChange}
            placeholder={tr("placeholder_new_password", "Enter new password")}
            required
          />
        </Form.Group>

        {/* Submit */}
        <Button
          type="submit"
          style={{
            width: "100%",
            borderRadius: "1rem",
            marginTop: "1rem",
            backgroundColor: "#007bff",
            border: "none",
          }}
        >
          {tr("reset_password", "Reset Password")}
        </Button>
      </Form>
    </div>
  );
};

export default ResetPassword;
