import React, { useEffect, useState } from "react";

const ToastAnimated = ({ message, type = "success", duration = 4000, onClose }) => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(true);
    const timer1 = setTimeout(() => setShow(false), duration); // يبدأ الإخفاء بعد المدة
    const timer2 = setTimeout(onClose, duration + 300); // ينادي onClose بعد انتهاء الحركة
    return () => {
      clearTimeout(timer1);
      clearTimeout(timer2);
    };
  }, [duration, onClose]);

  return (
    <div
      style={{
        position: "fixed",
        top: show ? "2rem" : "-100px",
        left: "50%",
        transform: "translateX(-50%)",
        backgroundColor: type === "success" ? "darkgreen" : "crimson",
        color: "white",
        padding: "1rem 2rem",
        borderRadius: "1rem",
        fontFamily: "Mulish",
        fontWeight: "bold",
        textAlign: "center",
        zIndex: 9999,
        transition: "top 0.5s ease, opacity 0.5s ease",
        opacity: show ? 1 : 0,
      }}
    >
      {message}
    </div>
  );
};

export default ToastAnimated;
