const translations = {
  en: {
    forgotTitle: "Forgot your Password?",
    forgotDesc: "Enter your email address and we'll send you a code to reset your password.",
    emailLabel: "Email Address",
    placeholder: "e.g. email@domain.com",
    btnReset: "Reset Password",
    success: "📩 Reset code sent to your email",
    error: "⚠️ Something went wrong, please try again",
  },
  ar: {
    forgotTitle: "هل نسيت كلمة المرور؟",
    forgotDesc: "أدخل بريدك الإلكتروني وسنرسل لك رمزًا لإعادة تعيين كلمة المرور.",
    emailLabel: "البريد الإلكتروني",
    placeholder: "مثال: email@domain.com",
    btnReset: "إعادة تعيين كلمة المرور",
