import React, { useState, useEffect } from "react";
import "./styles.css";
import "./mobile.css";
import Navbar from "./components/Navbar";
import Menu from "./components/Menu";
import Cart from "./components/Cart";
import Auth from "./components/auth";
import Sidebar from "./components/sideBar";
import PrivateRoutes from "./components/privateRoutes";
import DeleteItems from "./components/deleteItems";
import AddProduct from "./components/addProduct";
import Fail from "./components/fail";
import Forget from "./components/Forget";
import ResetPassword from "./components/resetPassword";
import Checkout from "./components/checkout";
import Success from "./components/success";
import NotFound from "./components/404";
import Contact from "./components/contact";
import Order from "./components/order";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

export default function App() {
  const [display, setDisplay] = useState(0);

  const changeDisplay = () => {
    setDisplay(display ^ 1);
  };

  // منع التمرير عند فتح الـ Sidebar
  useEffect(() => {
    if (display === 1) {
      document.body.style.overflow = "hidden";
      document.documentElement.scrollTop = 0;
    } else {
      document.body.style.overflow = "";
    }
  }, [display]);

  // رسالة عند عدم وجود الإنترنت
  useEffect(() => {
    if (!navigator.onLine) alert("You Are Offline");
  }, [navigator.onLine]);

  return (
    <Router>
      {display === 1 ? (
        <Sidebar display={display} changeDisplay={changeDisplay} />
      ) : (
        <>
          <Navbar display={display} changeDisplay={changeDisplay} />
          <Switch>
            <PrivateRoutes exact path="/" component={Menu} />
            <PrivateRoutes exact path="/cart" component={Cart} />
            <PrivateRoutes exact path="/addproduct" component={AddProduct} />

            <Route exact path="/forget" component={Forget} />
            <Route exact path="/auth" component={Auth} />
            <PrivateRoutes exact path="/checkout" component={Checkout} />
            <Route exact path="/success" component={Success} />
            <Route exact path="/fail" component={Fail} />
            <PrivateRoutes exact path="/deleteitems" component={DeleteItems} />
            <Route exact path="/forget/:code" component={ResetPassword} />
            <Route exact path="/contact" component={Contact} />
            <PrivateRoutes exact path="/order" component={Order} />
            <Route path="/" component={NotFound} />
          </Switch>
        </>
      )}
    </Router>
  );
}
