import React, { useState, useContext, useRef, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useLanguage } from "../context/LanguageContext";
import AuthContext from "../context/auth/authContext";
import Toast from "./Toast";
import "../styles.css";

const Forget = () => {
  const history = useHistory();
  const { t, lang } = useLanguage(); // للترجمة
  const { resetPassword } = useContext(AuthContext); // دالة ارسال ايميل اعادة تعيين كلمة السر
  const isMounted = useRef(true);

  // فورم البيانات
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [toast, setToast] = useState({ show: false, message: "", type: "success" });

  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  // تحديث حالة الايميل
  const handleEmailChange = (e) => setEmail(e.target.value);

  // ارسال طلب اعادة تعيين كلمة السر
  const handleSubmit = async (e) => {
    e.preventDefault();

    // تحقق اولي من الفارونت
    if (!email) {
      setToast({ show: true, message: t("error_fill_all"), type: "error" });
      return;
    }

    setIsLoading(true);

    try {
      // ارسال الطلب للباك، مع هيدر اللغة
      const res = await resetPassword(email, lang); // resetPassword يجب ان يكون في AuthContext

      // اظهار اشعار النجاح
      setToast({ show: true, message: res.message, type: "success" });

      // بعد ثواني قصيرة، تحويل المستخدم لصفحة تأكيد البريد
      setTimeout(() => {
        history.push("/check-email"); // ضع هنا الراوت الذي يعرض تأكيد البريد
      }, 2000);
    } catch (err) {
      const data = err.response?.data;
      if (data?.errors && data.errors.length > 0) {
        setToast({ show: true, message: data.errors[0].message, type: "error" });
      } else if (data?.message) {
        setToast({ show: true, message: data.message, type: "error" });
      } else {
        setToast({ show: true, message: t("error_server"), type: "error" });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container forgot-password-container">
      {/* Loader */}
      {isLoading && <div className="loader"></div>}

      {/* Toast Notifications */}
      {toast.show && <Toast show={toast.show} message={toast.message} type={toast.type} onClose={() => setToast({ ...toast, show: false })} />}

      {/* عنوان الصفحة */}
      <h2 className="title">{t("reset_password")}</h2>
      <p>{t("forgot_password")}</p>

      {/* الفورم */}
      <form onSubmit={handleSubmit}>
        {/* Email input */}
        <div className="input-field">
          <i className="fas fa-envelope"></i>
          <input
            type="email"
            name="email"
            placeholder={t("email")}
            value={email}
            onChange={handleEmailChange}
          />
        </div>

        {/* زر ارسال */}
        <button type="submit" className="btn">
          {t("reset_password")}
        </button>
      </form>
    </div>
  );
};

export default Forget;
