import React from "react";
import { Toast } from "react-bootstrap";

const GlobalToast = ({ show, onClose, type = "success", children }) => {
  const bg = type === "success" ? "#198754" : type === "warning" ? "#f0ad4e" : "#dc3545";
  return (
    <Toast
      onClose={onClose}
      show={show}
      delay={3500}
      autohide
      style={{
        position: "fixed",
        top: 20,
        left: "50%",
        transform: "translateX(-50%)",
        zIndex: 9999,
        background: bg,
        color: "#fff",
        borderRadius: 12,
        boxShadow: "0 10px 30px rgba(0,0,0,.25)"
      }}
    >
      <Toast.Body style={{ fontWeight: 600 }}>{children}</Toast.Body>
    </Toast>
  );
};

export default GlobalToast;
