import React, { useState, useContext, useEffect, useRef } from "react";
import { Link, useHistory } from "react-router-dom";
import AuthContext from "../context/auth/authContext";
import CartContext from "../context/cart/cartContext";
import LoadingContext from "../context/loading/loadingContext";
import { useToast } from "../components/GlobalToast"; // ‚úÖ ÿßŸÑÿ™Ÿàÿ≥ÿ™ ÿßŸÑÿπÿßŸÑŸÖŸä
import "../auth.css";
import { useLanguage } from "../context/LanguageContext";

const Auth = () => {
  const history = useHistory();
  const { register, login, isAuthenticated } = useContext(AuthContext);
  const { loadCart } = useContext(CartContext);
  const { Loader } = useContext(LoadingContext);
  const { showToast } = useToast(); // ‚úÖ ÿßÿ≥ÿ™ÿØÿπÿßÿ° ÿßŸÑÿ™Ÿàÿ≥ÿ™
  const { t } = useLanguage();
  const isMounted = useRef(true);

  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  const [flag, setFlag] = useState(0); // 0 = login, 1 = signup
  const [signUpData, setSignUpData] = useState({
    fullName: "",
    email: "",
    password: "",
    gender: "",
    phone: "",
    passportNumber: "",
  });
  const [signInData, setSignInData] = useState({ email: "", password: "" });
  const [inputType, setInputType] = useState("password");
  const [isLoading, setIsLoading] = useState(false);
  const [showVerification, setShowVerification] = useState(false);

  useEffect(() => {
    if (isAuthenticated) {
      loadCart();
      history.push("/");
    }
  }, [isAuthenticated, history, loadCart]);

  const handleSignUpChange = (e) =>
    setSignUpData({ ...signUpData, [e.target.name]: e.target.value });

  const handleSignInChange = (e) =>
    setSignInData({ ...signInData, [e.target.name]: e.target.value });

  const togglePassword = () =>
    setInputType(inputType === "password" ? "text" : "password");

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    const { fullName, email, password, gender, phone, passportNumber } = signUpData;

    if (!fullName || !email || !password || !gender || !phone || !passportNumber) {
      showToast(t("errors.fillAllFields"), "error");
      return;
    }

    setIsLoading(true);
    try {
      const res = await register(signUpData);
      if (res?.message) {
        showToast(res.message, "success");
      }
      setShowVerification(true);

      setTimeout(() => {
        setShowVerification(false);
        setFlag(0); // Ÿäÿ±ÿ¨ÿπ ŸÑÿµŸÅÿ≠ÿ© login
      }, 10000);
    } catch (err) {
      const msg =
        err.response?.data?.message ||
        err.message ||
        t("errors.default");
      showToast(msg, "error");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignInSubmit = async (e) => {
    e.preventDefault();
    const { email, password } = signInData;

    if (!email || !password) {
      showToast(t("errors.fillAllFields"), "error");
      return;
    }

    setIsLoading(true);
    try {
      const res = await login(signInData);
      if (res?.message) {
        showToast(res.message, "success");
      }
    } catch (err) {
      const msg =
        err.response?.data?.message ||
        err.message ||
        t("errors.default");
      showToast(msg, "error");
    } finally {
      setIsLoading(false);
    }
  };

  const toggleForm = () => setFlag(flag ^ 1);

  return (
    <>
      {isLoading && <Loader />}

      <div className={`container ${flag === 1 ? "sign-up-mode" : ""}`}>
        <div className="forms-container">
          <div className="signin-signup">
            {/* ==== Sign in ==== */}
            <form onSubmit={handleSignInSubmit} className="sign-in-form">
              <h2 className="title">{t("auth.signIn")}</h2>
              <div className="input-field">
                <i className="fas fa-envelope" />
                <input
                  type="email"
                  name="email"
                  placeholder={t("auth.email")}
                  value={signInData.email}
                  onChange={handleSignInChange}
                />
              </div>
              <div className="input-field"><i className="fas fa-lock" />
                <input
                  type={inputType}
                  name="password"
                  placeholder={t("auth.password")}
                  value={signInData.password}
                  onChange={handleSignInChange}
                />
                <span onClick={togglePassword} className="toggle-password">
                  {inputType === "password" ? "üëÅÔ∏è" : "üôà"}
                </span>
              </div>
              <input type="submit" value={t("auth.signIn")} className="btn solid" />
              <Link to="/forget">{t("auth.forgotPassword")}</Link>
            </form>

            {/* ==== Sign up ==== */}
            <form onSubmit={handleSignUpSubmit} className="sign-up-form">
              <h2 className="title">{t("auth.signUp")}</h2>
              <div className="input-field">
                <i className="fas fa-user" />
                <input
                  name="fullName"
                  type="text"
                  placeholder={t("auth.fullName")}
                  value={signUpData.fullName}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-envelope" />
                <input
                  name="email"
                  type="email"
                  placeholder={t("auth.email")}
                  value={signUpData.email}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field select-wrapper">
                <i className="fas fa-venus-mars" />
                <select
                  name="gender"
                  value={signUpData.gender}
                  onChange={handleSignUpChange}
                >
                  <option value="">{t("auth.selectGender")}</option>
                  <option value="male">{t("auth.male")}</option>
                  <option value="female">{t("auth.female")}</option>
                  <option value="other">{t("auth.other")}</option>
                </select>
              </div>
              <div className="input-field">
                <i className="fas fa-phone" />
                <input
                  name="phone"
                  type="text"
                  placeholder={t("auth.phone")}
                  value={signUpData.phone}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-id-card" />
                <input
                  name="passportNumber"
                  type="text"
                  placeholder={t("auth.passportNumber")}
                  value={signUpData.passportNumber}
                  onChange={handleSignUpChange}
                />
              </div>
              <div className="input-field">
                <i className="fas fa-lock" />
                <input
                  name="password"
                  type="password"
                  placeholder={t("auth.password")}
                  value={signUpData.password}
                  onChange={handleSignUpChange}
                />
              </div>
              <input type="submit" className="btn" value={t("auth.signUp")} />
            </form>
          </div>
        </div>

        {/* ===== Panels ===== */}
        <div className="panels-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>{t("auth.newHere")}</h3>
              <p>{t("auth.signupText")}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {t("auth.signUp")}
              </button>
            </div>
          </div>
          <div className="panel right-panel">
            <div className="content">
              <h3>{t("auth.oneOfUs")}</h3>
              <p>{t("auth.signinText")}</p>
              <button className="btn transparent" onClick={toggleForm}>
                {t("auth.signIn")}
              </button></div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Auth;
