import React, { useState } from "react";
import { Form, Button, Toast } from "react-bootstrap";
import "../styles.css";
import axios from "axios";

const ResetPassword = () => {
  const [email, setEmail] = useState("");
  const [code, setCode] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [status, setStatus] = useState({ val: 0, msg: "", type: "" });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email || !code || !newPassword) return alert("Fill all fields");

    try {
      await axios.post("http://localhost:5000/api/user/reset-password", { email, code, newPassword });
      setStatus({ val: 1, msg: "Password reset successfully!", type: "darkGreen" });

      setTimeout(() => setStatus({ val: 0, msg: "", type: "" }), 3000);
    } catch (e) {
      setStatus({ val: 1, msg: e.response?.data || "Error occurred", type: "red" });
      setTimeout(() => setStatus({ val: 0, msg: "", type: "" }), 3000);
    }
  };

  const Alert = () => (
    <Toast style={{ position: "fixed", right: "0", zIndex: "9899899" }}>
      <Toast.Header
        style={{
          background: status.type === "darkGreen" ? "green" : "red",
          color: "white",
          fontWeight: "bold"
        }}
      >
        <strong className="mr-auto">{status.type === "darkGreen" ? "Success" : "Fail"}</strong>
      </Toast.Header>
      <Toast.Body>{status.msg}</Toast.Body>
    </Toast>
  );

  return (
    <div className="forget">
      {status.val ? <Alert /> : null}
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>Reset Password</h4>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Code</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter the code"
            value={code}
            onChange={(e) => setCode(e.target.value)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="New password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </Form.Group>
        <Button type="submit" className="btn btn-primary" style={{ borderRadius: "1vh", textTransform: "capitalize" }}>
          Reset Password
        </Button>
      </Form>
    </div>
  );
};

export default ResetPassword;
