import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { useToast } from "../components/GlobalToast"; // استدعاء التوست
import "../styles.css";

const Forget = () => {
  const [mail, setMail] = useState("");
  const history = useHistory();
  const { showToast } = useToast();

  const handleChange = (e) => {
    setMail(e.target.value);
  };

  const handleSubmit = async (e) => {
  e.preventDefault();

  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };

  const formData = { email: mail };

  try {
    // إرسال البريد للباك
    const res = await axios.post(
      "http://localhost:5000/api/user/forgot-password",
      formData,
      config
    );

    // إشعار نجاح بالرسالة اللي جاية من الباك
    showToast(res.data.message || "📩 تم إرسال رمز إعادة التعيين إلى بريدك الإلكتروني", "success");

    // بعد ٣ ثواني يروح لصفحة reset
    setTimeout(() => {
      history.push("/reset-password");
    }, 3000);

  }catch (err) {
  let msg = "⚠️ حدث خطأ، حاول مرة أخرى";

  if (err.response?.data?.message) {
    // إذا الباك إند رجّع رسالة واضحة
    msg = err.response.data.message;
  } else if (err.message) {
    // إذا المشكلة بالشبكة أو Axios نفسه
    msg = err.message.includes("Network")
      ? "🚫 لا يوجد اتصال بالسيرفر"
      : "⚠️ خطأ في الاتصال، تحقق من الرابط";
  }

  showToast(msg, "error");
}
};

  return (
    <>
      <h4 style={{ marginTop: "6vh", fontFamily: "Mulish" }}>
        Forgot your Password?
      </h4>
      <div className="forget">
        <p>
          <LockIcon style={{ fontSize: "1.2rem", color: "grey" }} />
          <span style={{ fontFamily: "Poppins", fontSize: "0.95rem" }}>
            {" "}
            Enter your email address and we'll send you a code to reset your
            password.
          </span>
        </p>
        <br />
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label
              style={{
                fontFamily: "Poppins",
                align: "left",
                fontWeight: "550",
              }}
            >
              Email Address <MailOutlineIcon />
            </Form.Label>
            <Form.Control
              type="email"
              style={{ width: "125%" }}
              placeholder="e.g. email@domain.com"
              onChange={handleChange}
              required
            />
          </Form.Group>

          <Button
            className="btn btn-primary"
            variant="primary"
            type="submit"
            style={{
              borderRadius: "1vh",
              textTransform: "capitalize",
            }}
          >
            Reset Password
          </Button>
        </Form>
      </div>
    </>
  );
};

export default Forget;
