const AuthReducer = (state, action) => {
  switch (action.type) {
    case "REGISTER_SUCCESS":
    case "LOGIN_SUCCESS":
      return { ...state, token: action.payload.access_token, isAuthenticated: true};
    case "REGISTER_FAIL":
    case "LOGIN_FAIL":
    case "LOGOUT":
      return { ...state, token: null, isAuthenticated: false, user: null };
    default:
      return state;
  }
};

export default AuthReducer;
