import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import LockIcon from "@material-ui/icons/Lock";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import { useHistory } from "react-router-dom";
import { useToast } from "../components/GlobalToast";
import API from "../api/axios";
import translations from "../i18n/translations";
import "../styles.css";

const ResetPassword = () => {
  const history = useHistory();
  const { showToast } = useToast();
  const [formData, setFormData] = useState({
    email: "",
    resetCode: "",
    newPassword: "",
  });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { email, resetCode, newPassword } = formData;

    if (!email || !resetCode || !newPassword) {
      showToast("⚠️ Please fill all fields", "error");
      return;
    }

    try {
      const res = await API.post("/user/reset-password", formData);

      const lang = res.data.lang || "en";
      const t = translations[lang];

      showToast(res.data.message || t.success, "success");

      // إعادة التوجيه بعد ٣ ثواني
      setTimeout(() => {
        history.push("/auth");
      }, 3000);

    } catch (err) {
      const lang = err.response?.data?.lang || "en";
      const t = translations[lang];
      const msg = err.response?.data?.message || err.customMessage || t.error;
      showToast(msg, "error");
    }
  };

  const t = translations["en"]; // الافتراضي EN

  return (
    <div className="reset-password-container" style={{ marginTop: "5vh" }}>
      <h3 style={{ textAlign: "center", fontFamily: "Mulish" }}>
        Reset Your Password
      </h3>

      <Form onSubmit={handleSubmit} style={{ maxWidth: "400px", margin: "auto" }}>
        <Form.Group>
          <Form.Label>
            {t.emailLabel} <MailOutlineIcon />
          </Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder={t.placeholder}
            required
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>
            Verification Code <LockIcon />
          </Form.Label>
          <Form.Control
            type="text"
            name="resetCode"
            value={formData.resetCode}
            onChange={handleChange}
            placeholder="Enter the code you received"
            required
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>
            New Password <LockIcon />
          </Form.Label>
          <Form.Control
            type="password"
            name="newPassword"
            value={formData.newPassword}
            onChange={handleChange}
            placeholder="Enter new password"
            required
          />
        </Form.Group>

        <Button
          type="submit"
          style={{
            width: "100%",
            borderRadius: "1rem",
            marginTop: "1rem",
            backgroundColor: "#007bff",
            border: "none",
          }}
        >
          {t.btnReset}
        </Button>
      </Form>
    </div>
  );
};

export default ResetPassword;
